	get : function(){
		if (this._synced){
			if (Tone.Transport.state === Tone.State.Started){
				return this._state.getValueAtTime(Tone.Transport.seconds);
			} else {
				return Tone.State.Stopped;
			}
		} else {
			return this._state.getValueAtTime(this.now());
		}
	}
