import "../signal/Signal";
import "../core/Gain";

/**
 *  @class Add a signal and a number or two signals. When no value is
 *         passed into the constructor, Tone.Add will sum <code>input[0]</code>
 *         and <code>input[1]</code>. If a value is passed into the constructor, 
 *         the it will be added to the input.
 *  
 *  @constructor
 *  @extends {Tone.Signal}
 *  @param {number=} value If no value is provided, Tone.Add will sum the first
 *                         and second inputs. 
 *  @example
 * var signal = new Tone.Signal(2);
 * var add = new Tone.Add(2);
 * signal.connect(add);
 * //the output of add equals 4
 *  @example
 * //if constructed with no arguments
 * //it will add the first and second inputs
 * var add = new Tone.Add();
 * var sig0 = new Tone.Signal(3).connect(add, 0, 0);
 * var sig1 = new Tone.Signal(4).connect(add, 0, 1);
 * //the output of add equals 7. 
 */
Tone.Add = function(value){

	Tone.Signal.call(this);
	this.createInsOuts(2, 0);

	/**
	 *  the summing node
	 *  @type {GainNode}
	 *  @private
	 */
	this._sum = this.input[0] = this.input[1] = this.output = new Tone.Gain();

	/**
	 *  @private
	 *  @type {Tone.Signal}
	 */
	this._param = this.input[1] = new Tone.Signal(value);

	this._param.connect(this._sum);
};

Tone.extend(Tone.Add, Tone.Signal);

/**
 *  Clean up.
 *  @returns {Tone.Add} this
 */
Tone.Add.prototype.dispose = function(){
	Tone.Signal.prototype.dispose.call(this);
	this._sum.dispose();
	this._sum = null;
	return this;
}; 

export default Tone.Add;

import Tone from "../core/Tone";
import "../core/AudioNode";

/**
 *  @class  Base class for all Signals. Used Internally.
 *
 *  @constructor
 *  @extends {Tone}
 */
Tone.SignalBase = function(){
	Tone.AudioNode.call(this);
};

Tone.extend(Tone.SignalBase, Tone.AudioNode);

/**
 *  When signals connect to other signals or AudioParams,
 *  they take over the output value of that signal or AudioParam.
 *  For all other nodes, the behavior is the same as a default <code>connect</code>.
 *
 *  @override
 *  @param {AudioParam|AudioNode|Tone.Signal|Tone} node
 *  @param {number} [outputNumber=0] The output number to connect from.
 *  @param {number} [inputNumber=0] The input number to connect to.
 *  @returns {Tone.SignalBase} this
 */
Tone.SignalBase.prototype.connect = function(node, outputNumber, inputNumber){
	//zero it out so that the signal can have full control
	if ((Tone.Signal && Tone.Signal === node.constructor) ||
			(Tone.Param && Tone.Param === node.constructor)){
		//cancel changes
		node._param.cancelScheduledValues(0);
		//reset the value
		node._param.setValueAtTime(0, 0);
		//mark the value as overridden
		node.overridden = true;
	} else if (node instanceof AudioParam){
		node.cancelScheduledValues(0);
		node.setValueAtTime(0, 0);
	}
	Tone.AudioNode.prototype.connect.call(this, node, outputNumber, inputNumber);
	return this;
};

export default Tone.SignalBase;

import Tone from "../core/Tone";
import "../component/Envelope";
import "../core/Gain";

/**
 *  @class  Tone.AmplitudeEnvelope is a Tone.Envelope connected to a gain node.
 *          Unlike Tone.Envelope, which outputs the envelope's value, Tone.AmplitudeEnvelope accepts
 *          an audio signal as the input and will apply the envelope to the amplitude
 *          of the signal. Read more about ADSR Envelopes on [Wikipedia](https://en.wikipedia.org/wiki/Synthesizer#ADSR_envelope).
 *
 *  @constructor
 *  @extends {Tone.Envelope}
 *  @param {Time|Object} [attack] The amount of time it takes for the envelope to go from
 *                               0 to it's maximum value.
 *  @param {Time} [decay]	The period of time after the attack that it takes for the envelope
 *                       	to fall to the sustain value.
 *  @param {NormalRange} [sustain]	The percent of the maximum value that the envelope rests at until
 *                                	the release is triggered.
 *  @param {Time} [release]	The amount of time after the release is triggered it takes to reach 0.
 *  @example
 * var ampEnv = new Tone.AmplitudeEnvelope({
 * 	"attack": 0.1,
 * 	"decay": 0.2,
 * 	"sustain": 1.0,
 * 	"release": 0.8
 * }).toMaster();
 * //create an oscillator and connect it
 * var osc = new Tone.Oscillator().connect(ampEnv).start();
 * //trigger the envelopes attack and release "8t" apart
 * ampEnv.triggerAttackRelease("8t");
 */
Tone.AmplitudeEnvelope = function(){

	Tone.Envelope.apply(this, arguments);

	/**
	 *  the input node
	 *  @type {GainNode}
	 *  @private
	 */
	this.input = this.output = new Tone.Gain();

	this._sig.connect(this.output.gain);
};

Tone.extend(Tone.AmplitudeEnvelope, Tone.Envelope);

/**
 *  Clean up
 *  @return  {Tone.AmplitudeEnvelope}  this
 */
Tone.AmplitudeEnvelope.prototype.dispose = function(){
	Tone.Envelope.prototype.dispose.call(this);
	return this;
};

export default Tone.AmplitudeEnvelope;

import Tone from "../core/Tone";
