/**
 *  Return the value of the frequency in Scientific Pitch Notation
 *  @return  {Note}
 *  @example
 * Tone.Frequency(69, "midi").toNote(); //"A4"
 */
Tone.Frequency.prototype.toNote = function(){
	var freq = this.toFrequency();
	var log = Math.log2(freq / Tone.Frequency.A4);
	var noteNumber = Math.round(12 * log) + 57;
	var octave = Math.floor(noteNumber/12);
	if (octave < 0){
		noteNumber += -12 * octave;
	}
	var noteName = scaleIndexToNote[noteNumber % 12];
	return noteName + octave.toString();
};

/**
 *  Return the duration of one cycle in seconds.
 *  @return  {Seconds}
 */
Tone.Frequency.prototype.toSeconds = function(){
	return 1 / Tone.TimeBase.prototype.toSeconds.call(this);
};

/**
 *  Return the value in Hertz
 *  @return  {Frequency}
 */
Tone.Frequency.prototype.toFrequency = function(){
	return Tone.TimeBase.prototype.toFrequency.call(this);
};

/**
 *  Return the duration of one cycle in ticks
 *  @return  {Ticks}
 */
Tone.Frequency.prototype.toTicks = function(){
	var quarterTime = this._beatsToUnits(1);
	var quarters = this.valueOf() / quarterTime;
	return Math.floor(quarters * Tone.Transport.PPQ);
};

///////////////////////////////////////////////////////////////////////////
//	UNIT CONVERSIONS HELPERS
///////////////////////////////////////////////////////////////////////////

/**
 *  With no arguments, return 0
 *  @return  {Number}
 *  @private
 */
Tone.Frequency.prototype._noArg = function(){
	return 0;
};

/**
 *  Returns the value of a frequency in the current units
 *  @param {Frequency} freq
 *  @return  {Number}
 *  @private
 */
Tone.Frequency.prototype._frequencyToUnits = function(freq){
	return freq;
};

/**
 *  Returns the value of a tick in the current time units
 *  @param {Ticks} ticks
 *  @return  {Number}
 *  @private
 */
Tone.Frequency.prototype._ticksToUnits = function(ticks){
	return 1 / ((ticks * 60) / (Tone.Transport.bpm.value * Tone.Transport.PPQ));
};

/**
 *  Return the value of the beats in the current units
 *  @param {Number} beats
 *  @return  {Number}
 *  @private
 */
Tone.Frequency.prototype._beatsToUnits = function(beats){
	return 1 / Tone.TimeBase.prototype._beatsToUnits.call(this, beats);
};

/**
 *  Returns the value of a second in the current units
 *  @param {Seconds} seconds
 *  @return  {Number}
 *  @private
 */
Tone.Frequency.prototype._secondsToUnits = function(seconds){
	return 1 / seconds;
};

/**
 *  The default units if none are given.
 *  @private
 */
Tone.Frequency.prototype._defaultUnits = "hz";

///////////////////////////////////////////////////////////////////////////
//	FREQUENCY CONVERSIONS
///////////////////////////////////////////////////////////////////////////

/**
 *  Note to scale index
 *  @type  {Object}
 *  @private
 */
var noteToScaleIndex = {
	"cbb" : -2, "cb" : -1, "c" : 0, "c#" : 1, "cx" : 2,
	"dbb" : 0, "db" : 1, "d" : 2, "d#" : 3, "dx" : 4,
	"ebb" : 2, "eb" : 3, "e" : 4, "e#" : 5, "ex" : 6,
	"fbb" : 3, "fb" : 4, "f" : 5, "f#" : 6, "fx" : 7,
	"gbb" : 5, "gb" : 6, "g" : 7, "g#" : 8, "gx" : 9,
	"abb" : 7, "ab" : 8, "a" : 9, "a#" : 10, "ax" : 11,
	"bbb" : 9, "bb" : 10, "b" : 11, "b#" : 12, "bx" : 13,
};

/**
 *  scale index to note (sharps)
 *  @type  {Array}
 *  @private
 */
var scaleIndexToNote = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"];

/**
 *  The [concert pitch](https://en.wikipedia.org/wiki/Concert_pitch)
 *  A4's values in Hertz.
 *  @type {Frequency}
 *  @static
 */
Tone.Frequency.A4 = 440;

/**
 *  Convert a MIDI note to frequency value.
 *  @param  {MIDI} midi The midi number to convert.
 *  @return {Frequency} the corresponding frequency value
 *  @static
 *  @example
 * Tone.Frequency.mtof(69); // returns 440
 */
Tone.Frequency.mtof = function(midi){
	return Tone.Frequency.A4 * Math.pow(2, (midi - 69) / 12);
};

/**
 *  Convert a frequency value to a MIDI note.
 *  @param {Frequency} frequency The value to frequency value to convert.
 *  @returns  {MIDI}
 *  @static
 *  @example
 * Tone.Frequency.ftom(440); // returns 69
 */
Tone.Frequency.ftom = function(frequency){
	return 69 + Math.round(12 * Math.log2(frequency / Tone.Frequency.A4));
};

export default Tone.Frequency;

import Tone from "../core/Tone";
import "../signal/Signal";
import "../signal/Pow";
import "../type/Type";
import "../core/AudioNode";

/**
 *  @class  Tone.Envelope is an [ADSR](https://en.wikipedia.org/wiki/Synthesizer#ADSR_envelope)
 *          envelope generator. Tone.Envelope outputs a signal which
 *          can be connected to an AudioParam or Tone.Signal.
 *          <img src="https://upload.wikimedia.org/wikipedia/commons/e/ea/ADSR_parameter.svg">
 *
 *  @constructor
 *  @extends {Tone.AudioNode}
 *  @param {Time} [attack] The amount of time it takes for the envelope to go from
 *                         0 to it's maximum value.
 *  @param {Time} [decay]	The period of time after the attack that it takes for the envelope
 *                       	to fall to the sustain value. Value must be greater than 0.
 *  @param {NormalRange} [sustain]	The percent of the maximum value that the envelope rests at until
 *                                	the release is triggered.
 *  @param {Time} [release]	The amount of time after the release is triggered it takes to reach 0.
 *                         	Value must be greater than 0.
 *  @example
 * //an amplitude envelope
 * var gainNode = Tone.context.createGain();
 * var env = new Tone.Envelope({
 * 	"attack" : 0.1,
 * 	"decay" : 0.2,
 * 	"sustain" : 1,
 * 	"release" : 0.8,
 * });
 * env.connect(gainNode.gain);
 */
Tone.Envelope = function(){

	//get all of the defaults
	var options = Tone.defaults(arguments, ["attack", "decay", "sustain", "release"], Tone.Envelope);
	Tone.AudioNode.call(this);

	/**
	 *  When triggerAttack is called, the attack time is the amount of
	 *  time it takes for the envelope to reach it's maximum value.
	 *  @type {Time}
	 */
	this.attack = options.attack;

	/**
	 *  After the attack portion of the envelope, the value will fall
	 *  over the duration of the decay time to it's sustain value.
	 *  @type {Time}
	 */
	this.decay = options.decay;

	/**
	 * 	The sustain value is the value
	 * 	which the envelope rests at after triggerAttack is
	 * 	called, but before triggerRelease is invoked.
	 *  @type {NormalRange}
	 */
	this.sustain = options.sustain;

	/**
	 *  After triggerRelease is called, the envelope's
	 *  value will fall to it's miminum value over the
	 *  duration of the release time.
	 *  @type {Time}
	 */
	this.release = options.release;

	/**
	 *  the next time the envelope is at standby
	 *  @type {number}
	 *  @private
	 */
	this._attackCurve = "linear";

	/**
	 *  the next time the envelope is at standby
	 *  @type {number}
	 *  @private
	 */
	this._releaseCurve = "exponential";

	/**
	 *  the signal
	 *  @type {Tone.Signal}
	 *  @private
	 */
	this._sig = this.output = new Tone.Signal(0);

	//set the attackCurve initially
	this.attackCurve = options.attackCurve;
	this.releaseCurve = options.releaseCurve;
	this.decayCurve = options.decayCurve;
};

Tone.extend(Tone.Envelope, Tone.AudioNode);

/**
 *  the default parameters
 *  @static
 *  @const
 */
Tone.Envelope.defaults = {
	"attack" : 0.01,
	"decay" : 0.1,
	"sustain" : 0.5,
	"release" : 1,
	"attackCurve" : "linear",
	"decayCurve" : "exponential",
	"releaseCurve" : "exponential",
};

/**
 * Read the current value of the envelope. Useful for
 * syncronizing visual output to the envelope.
 * @memberOf Tone.Envelope#
 * @type {Number}
 * @name value
 * @readOnly
 */
Object.defineProperty(Tone.Envelope.prototype, "value", {
	get : function(){
		return this.getValueAtTime(this.now());
	}
});

/**
 *  Get the curve
 *  @param  {Array|String}  curve
 *  @param  {String}  direction  In/Out
 *  @return  {String}  The curve name
 *  @private
 */
Tone.Envelope.prototype._getCurve = function(curve, direction){
	if (Tone.isString(curve)){
		return curve;
	} else if (Tone.isArray(curve)){
		//look up the name in the curves array
		for (var t in Tone.Envelope.Type){
			if (Tone.Envelope.Type[t][direction] === curve){
				return t;
			}
		}
	}
};

/**
 *  Assign a the curve to the given name using the direction
 *  @param  {String}  name
 *  @param  {String}  direction In/Out
 *  @param  {Array}  curve
 *  @private
 */
Tone.Envelope.prototype._setCurve = function(name, direction, curve){
	//check if it's a valid type
	if (Tone.Envelope.Type.hasOwnProperty(curve)){
		var curveDef = Tone.Envelope.Type[curve];
		if (Tone.isObject(curveDef)){
			this[name] = curveDef[direction];
		} else {
			this[name] = curveDef;
		}
	} else if (Tone.isArray(curve)){
		this[name] = curve;
	} else {
		throw new Error("Tone.Envelope: invalid curve: " + curve);
	}
};

/**
 * The shape of the attack.
 * Can be any of these strings:
 * <ul>
 *   <li>linear</li>
 *   <li>exponential</li>
 *   <li>sine</li>
 *   <li>cosine</li>
 *   <li>bounce</li>
 *   <li>ripple</li>
 *   <li>step</li>
 * </ul>
 * Can also be an array which describes the curve. Values
 * in the array are evenly subdivided and linearly
 * interpolated over the duration of the attack.
 * @memberOf Tone.Envelope#
 * @type {String|Array}
 * @name attackCurve
 * @example
 * env.attackCurve = "linear";
 * @example
 * //can also be an array
 * env.attackCurve = [0, 0.2, 0.3, 0.4, 1]
 */
Object.defineProperty(Tone.Envelope.prototype, "attackCurve", {
	get : function(){
		return this._getCurve(this._attackCurve, "In");
	},
	set : function(curve){
		this._setCurve("_attackCurve", "In", curve);
	}
});

/**
 * The shape of the release. See the attack curve types.
 * @memberOf Tone.Envelope#
 * @type {String|Array}
 * @name releaseCurve
 * @example
 * env.releaseCurve = "linear";
 */
Object.defineProperty(Tone.Envelope.prototype, "releaseCurve", {
	get : function(){
		return this._getCurve(this._releaseCurve, "Out");
	},
	set : function(curve){
		this._setCurve("_releaseCurve", "Out", curve);
	}
});

/**
 * The shape of the decay either "linear" or "exponential"
 * @memberOf Tone.Envelope#
 * @type {String}
 * @name decayCurve
 * @example
 * env.decayCurve = "linear";
 */
Object.defineProperty(Tone.Envelope.prototype, "decayCurve", {
	get : function(){
		return this._decayCurve;
	},
	set : function(curve){
		var curves = ["linear", "exponential"];
		if (!curves.includes(curve)){
			throw new Error("Tone.Envelope: invalid curve: " + curve);
		} else {
			this._decayCurve = curve;
		}
	}
});

/**
 *  Trigger the attack/decay portion of the ADSR envelope.
 *  @param  {Time} [time=now] When the attack should start.
 *  @param {NormalRange} [velocity=1] The velocity of the envelope scales the vales.
 *                               number between 0-1
 *  @returns {Tone.Envelope} this
 *  @example
 *  //trigger the attack 0.5 seconds from now with a velocity of 0.2
 *  env.triggerAttack("+0.5", 0.2);
 */
Tone.Envelope.prototype.triggerAttack = function(time, velocity){
	this.log("triggerAttack", time, velocity);
	time = this.toSeconds(time);
	var originalAttack = this.toSeconds(this.attack);
	var attack = originalAttack;
	var decay = this.toSeconds(this.decay);
	velocity = Tone.defaultArg(velocity, 1);
	//check if it's not a complete attack
	var currentValue = this.getValueAtTime(time);
	if (currentValue > 0){
		//subtract the current value from the attack time
		var attackRate = 1 / attack;
		var remainingDistance = 1 - currentValue;
		//the attack is now the remaining time
		attack = remainingDistance / attackRate;
	}
	//attack
	if (attack === 0){
		//case where the attack time is 0 should set instantly
		this._sig.setValueAtTime(velocity, time);
	} else if (this._attackCurve === "linear"){
		this._sig.linearRampTo(velocity, attack, time);
	} else if (this._attackCurve === "exponential"){
		this._sig.targetRampTo(velocity, attack, time);
	} else if (attack > 0){
		this._sig.cancelAndHoldAtTime(time);
		var curve = this._attackCurve;
		//find the starting position in the curve
		for (var i = 1; i < curve.length; i++){
			//the starting index is between the two values
			if (curve[i-1] <= currentValue && currentValue <= curve[i]){
				curve = this._attackCurve.slice(i);
				//the first index is the current value
				curve[0] = currentValue;
				break;
			}
		}
		this._sig.setValueCurveAtTime(curve, time, attack, velocity);
	}
	//decay
	if (decay){
		var decayValue = velocity * this.sustain;
		var decayStart = time + attack;
		this.log("decay", decayStart);
		if (this._decayCurve === "linear"){
			this._sig.linearRampTo(decayValue, decay, decayStart+this.sampleTime);
		} else if (this._decayCurve === "exponential"){
			this._sig.exponentialApproachValueAtTime(decayValue, decayStart, decay);
		}
	}
	return this;
};

/**
 *  Triggers the release of the envelope.
 *  @param  {Time} [time=now] When the release portion of the envelope should start.
 *  @returns {Tone.Envelope} this
 *  @example
 *  //trigger release immediately
 *  env.triggerRelease();
 */
Tone.Envelope.prototype.triggerRelease = function(time){
	this.log("triggerRelease", time);
	time = this.toSeconds(time);
	var currentValue = this.getValueAtTime(time);
	if (currentValue > 0){
		var release = this.toSeconds(this.release);
		if (this._releaseCurve === "linear"){
			this._sig.linearRampTo(0, release, time);
		} else if (this._releaseCurve === "exponential"){
			this._sig.targetRampTo(0, release, time);
		} else {
			var curve = this._releaseCurve;
			if (Tone.isArray(curve)){
				this._sig.cancelAndHoldAtTime(time);
				this._sig.setValueCurveAtTime(curve, time, release, currentValue);
			}
		}
	}
	return this;
};

/**
 *  Get the scheduled value at the given time. This will
 *  return the unconverted (raw) value.
 *  @param  {Number}  time  The time in seconds.
 *  @return  {Number}  The scheduled value at the given time.
 */
Tone.Envelope.prototype.getValueAtTime = function(time){
	return this._sig.getValueAtTime(time);
};

/**
 *  triggerAttackRelease is shorthand for triggerAttack, then waiting
 *  some duration, then triggerRelease.
 *  @param {Time} duration The duration of the sustain.
 *  @param {Time} [time=now] When the attack should be triggered.
 *  @param {number} [velocity=1] The velocity of the envelope.
 *  @returns {Tone.Envelope} this
 *  @example
 * //trigger the attack and then the release after 0.6 seconds.
 * env.triggerAttackRelease(0.6);
 */
Tone.Envelope.prototype.triggerAttackRelease = function(duration, time, velocity){
	time = this.toSeconds(time);
	this.triggerAttack(time, velocity);
	this.triggerRelease(time + this.toSeconds(duration));
	return this;
};

/**
 *  Cancels all scheduled envelope changes after the given time.
 *  @param  {Time} after
 *  @returns {Tone.Envelope} this
 */
Tone.Envelope.prototype.cancel = function(after){
	this._sig.cancelScheduledValues(after);
	return this;
};

/**
 *  Borrows the connect method from Tone.Signal.
 *  @function
 *  @private
 */
Tone.Envelope.prototype.connect = Tone.SignalBase.prototype.connect;

/**
 	 *  Generate some complex envelope curves.
 	 */
(function _createCurves(){

	var curveLen = 128;

	var i, k;

	//cosine curve
	var cosineCurve = [];
	for (i = 0; i < curveLen; i++){
		cosineCurve[i] = Math.sin((i / (curveLen - 1)) * (Math.PI / 2));
	}

	//ripple curve
	var rippleCurve = [];
	var rippleCurveFreq = 6.4;
	for (i = 0; i < curveLen - 1; i++){
		k = (i / (curveLen - 1));
		var sineWave = Math.sin(k * (Math.PI * 2) * rippleCurveFreq - Math.PI / 2) + 1;
		rippleCurve[i] = sineWave/10 + k * 0.83;
	}
	rippleCurve[curveLen - 1] = 1;

	//stairs curve
	var stairsCurve = [];
	var steps = 5;
	for (i = 0; i < curveLen; i++){
		stairsCurve[i] = Math.ceil((i / (curveLen - 1)) * steps) / steps;
	}

	//in-out easing curve
	var sineCurve = [];
	for (i = 0; i < curveLen; i++){
		k = i / (curveLen - 1);
		sineCurve[i] = 0.5 * (1 - Math.cos(Math.PI * k));
	}

	//a bounce curve
	var bounceCurve = [];
	for (i = 0; i < curveLen; i++){
		k = i / (curveLen - 1);
		var freq = Math.pow(k, 3) * 4 + 0.2;
		var val = Math.cos(freq * Math.PI * 2 * k);
		bounceCurve[i] = Math.abs(val * (1 - k));
	}

	/**
	 *  Invert a value curve to make it work for the release
	 *  @private
	 */
	function invertCurve(curve){
		var out = new Array(curve.length);
		for (var j = 0; j < curve.length; j++){
			out[j] = 1 - curve[j];
		}
		return out;
	}

	/**
	 *  reverse the curve
	 *  @private
	 */
	function reverseCurve(curve){
		return curve.slice(0).reverse();
	}

	/**
	 *  attack and release curve arrays
	 *  @type  {Object}
	 *  @private
	 */
	Tone.Envelope.Type = {
		"linear" : "linear",
		"exponential" : "exponential",
		"bounce" : {
			In : invertCurve(bounceCurve),
			Out : bounceCurve
		},
		"cosine" : {
			In : cosineCurve,
			Out : reverseCurve(cosineCurve)
		},
		"step" : {
			In : stairsCurve,
			Out : invertCurve(stairsCurve)
		},
		"ripple" : {
			In : rippleCurve,
			Out : invertCurve(rippleCurve)
		},
		"sine" : {
			In : sineCurve,
			Out : invertCurve(sineCurve)
		}
	};

})();

/**
 *  Disconnect and dispose.
 *  @returns {Tone.Envelope} this
 */
Tone.Envelope.prototype.dispose = function(){
	Tone.AudioNode.prototype.dispose.call(this);
	this._sig.dispose();
	this._sig = null;
	this._attackCurve = null;
	this._releaseCurve = null;
	return this;
};

export default Tone.Envelope;

import Tone from "../core/Tone";
import "../signal/WaveShaper";
import "../component/Merge";
import "../signal/Zero";
import "../component/Split";
import "../core/Gain";
import "../signal/Signal";
import "../core/Context";

if (Tone.supported && !Tone.global.AudioContext.prototype.createStereoPanner){

	/**
	 * @class Shimmed StereoPannerNode
	 * @param  {AudioContext} context
	 * @private
	 */
	var StereoPannerNode = function(context){

		/**
		 * The audio context
		 * @type {AudioContext}
		 */
		this.context = context;

		/**
		 * The left/right panning. [-1, 1]
		 * @type {AudioRange}
		 * @signal
		 */
		this.pan = new Tone.Signal(0, Tone.Type.AudioRange);

		/**
		 * Equal power scaling of the right gain
		 * @type {Tone.WaveShaper}
		 */
		var rightWaveShaper = new Tone.WaveShaper(function(val){
			return Tone.equalPowerScale((val+1)/2);
		}, 4096);

		/**
		 * Equal power scaling of the left gain
		 * @type {Tone.WaveShaper}
		 * @private
		 */
		var leftWaveShaper = new Tone.WaveShaper(function(val){
			return Tone.equalPowerScale(1 - (val+1)/2);
		}, 4096);

		/**
		 * The left gain value
		 * @type {Tone.Gain}
		 * @private
		 */
		var leftGain = new Tone.Gain();

		/**
		 * The right gain value
		 * @type {Tone.Gain}
		 * @private
		 */
		var rightGain = new Tone.Gain();

		/**
		 * Split the incoming signal
		 * @type {Tone.Split}
		 * @private
		 */
		var split = this.input = new Tone.Split();
		//fixes safari issue with splitting audio
		split._splitter.channelCountMode = "explicit";

		/**
		 * Keeps the waveshapers from optimizing 0s
		 * @type {Tone.Zero}
		 * @private
		 */
		var zero = new Tone.Zero();
		zero.fan(rightWaveShaper, leftWaveShaper);

		/**
		 * Merge the outgoing signal
		 * @type {Tone.Merge}
		 * @private
		 */
		var merge = this.output = new Tone.Merge();

		//connections
		split.left.chain(leftGain, merge.left);
		split.right.chain(rightGain, merge.right);
		this.pan.chain(leftWaveShaper, leftGain.gain);
		this.pan.chain(rightWaveShaper, rightGain.gain);
	};

	StereoPannerNode.prototype.disconnect = function(){
		this.output.disconnect.apply(this.output, arguments);
	};

	StereoPannerNode.prototype.connect = function(){
		this.output.connect.apply(this.output, arguments);
	};

	//add it to the AudioContext
	AudioContext.prototype.createStereoPanner = function(){
		return new StereoPannerNode(this);
	};
	Tone.Context.prototype.createStereoPanner = function(){
		return new StereoPannerNode(this);
	};
}

import Tone from "../core/Tone";
import "../component/CrossFade";
import "../component/Merge";
import "../component/Split";
import "../shim/StereoPannerNode";
import "../signal/Signal";
import "../signal/AudioToGain";
import "../signal/Zero";
import "../core/AudioNode";

/**
 *  @class  Tone.Panner is an equal power Left/Right Panner and does not
 *          support 3D. Panner uses the StereoPannerNode when available.
 *
 *  @constructor
 *  @extends {Tone.AudioNode}
 *  @param {NormalRange} [initialPan=0] The initail panner value (center).
 *  @example
 *  //pan the input signal hard right.
 *  var panner = new Tone.Panner(1);
 */
Tone.Panner = function(){

	var options = Tone.defaults(arguments, ["pan"], Tone.Panner);
	Tone.AudioNode.call(this);
	/**
	*  the panner node
	*  @type {StereoPannerNode}
	*  @private
	*/
	this._panner = this.input = this.output = this.context.createStereoPanner();

	/**
	*  The pan control. -1 = hard left, 1 = hard right.
	*  @type {AudioRange}
	*  @signal
	*/
	this.pan = this._panner.pan;

	//initial value
	this.pan.value = options.pan;
	this._readOnly("pan");
};

Tone.extend(Tone.Panner, Tone.AudioNode);

/**
 *  Defaults
 *  @type  {Object}
 *  @const
 *  @static
 */
Tone.Panner.defaults = {
	"pan" : 0
};

/**
 *  Clean up.
 *  @returns {Tone.Panner} this
 */
Tone.Panner.prototype.dispose = function(){
	Tone.AudioNode.prototype.dispose.call(this);
	this._writable("pan");
	this._panner.disconnect();
	this._panner = null;
	this.pan = null;
	return this;
};

