import Tone from "../core/Tone";
import "../source/Source";
import "../source/Oscillator";
import "../signal/Multiply";
import "../core/Gain";

/**
 *  @class Tone.FMOscillator
 *
 *  @extends {Tone.Source}
 *  @constructor
 *  @param {Frequency} frequency The starting frequency of the oscillator.
 *  @param {String} type The type of the carrier oscillator.
 *  @param {String} modulationType The type of the modulator oscillator.
 *  @example
 * //a sine oscillator frequency-modulated by a square wave
 * var fmOsc = new Tone.FMOscillator("Ab3", "sine", "square").toMaster().start();
 */
Tone.FMOscillator = function(){

	var options = Tone.defaults(arguments, ["frequency", "type", "modulationType"], Tone.FMOscillator);
	Tone.Source.call(this, options);

	/**
	 *  The carrier oscillator
	 *  @type {Tone.Oscillator}
	 *  @private
	 */
	this._carrier = new Tone.Oscillator(options.frequency, options.type);

	/**
	 *  The oscillator's frequency
	 *  @type {Frequency}
	 *  @signal
	 */
	this.frequency = new Tone.Signal(options.frequency, Tone.Type.Frequency);

	/**
	 *  The detune control signal.
	 *  @type {Cents}
	 *  @signal
	 */
	this.detune = this._carrier.detune;
	this.detune.value = options.detune;

	/**
	 *  The modulation index which is in essence the depth or amount of the modulation. In other terms it is the
	 *  ratio of the frequency of the modulating signal (mf) to the amplitude of the
	 *  modulating signal (ma) -- as in ma/mf.
	 *	@type {Positive}
	 *	@signal
	 */
	this.modulationIndex = new Tone.Multiply(options.modulationIndex);
	this.modulationIndex.units = Tone.Type.Positive;

	/**
	 *  The modulating oscillator
	 *  @type  {Tone.Oscillator}
	 *  @private
	 */
	this._modulator = new Tone.Oscillator(options.frequency, options.modulationType);

	/**
	 *  Harmonicity is the frequency ratio between the carrier and the modulator oscillators.
	 *  A harmonicity of 1 gives both oscillators the same frequency.
	 *  Harmonicity = 2 means a change of an octave.
	 *  @type {Positive}
	 *  @signal
	 *  @example
	 * //pitch the modulator an octave below carrier
	 * synth.harmonicity.value = 0.5;
	 */
	this.harmonicity = new Tone.Multiply(options.harmonicity);
	this.harmonicity.units = Tone.Type.Positive;

	/**
	 *  the node where the modulation happens
	 *  @type {Tone.Gain}
	 *  @private
	 */
	this._modulationNode = new Tone.Gain(0);

	//connections
	this.frequency.connect(this._carrier.frequency);
	this.frequency.chain(this.harmonicity, this._modulator.frequency);
	this.frequency.chain(this.modulationIndex, this._modulationNode);
	this._modulator.connect(this._modulationNode.gain);
	this._modulationNode.connect(this._carrier.frequency);
	this._carrier.connect(this.output);
	this.detune.connect(this._modulator.detune);

	this.phase = options.phase;

	this._readOnly(["modulationIndex", "frequency", "detune", "harmonicity"]);
};

Tone.extend(Tone.FMOscillator, Tone.Source);

/**
 *  default values
 *  @static
 *  @type {Object}
 *  @const
 */
Tone.FMOscillator.defaults = {
	"frequency" : 440,
	"detune" : 0,
	"phase" : 0,
	"type" : "sine",
	"modulationIndex" : 2,
	"modulationType" : "square",
	"harmonicity" : 1
};

/**
 *  start the oscillator
 *  @param  {Time} [time=now]
 *  @private
 */
Tone.FMOscillator.prototype._start = function(time){
	this._modulator.start(time);
	this._carrier.start(time);
};

/**
 *  stop the oscillator
 *  @param  {Time} time (optional) timing parameter
 *  @private
 */
Tone.FMOscillator.prototype._stop = function(time){
	this._modulator.stop(time);
	this._carrier.stop(time);
};

/**
 *  stop the oscillator
 *  @param  {Time} time (optional) timing parameter
 *  @private
 */
Tone.FMOscillator.prototype.restart = function(time){
	this._modulator.restart(time);
	this._carrier.restart(time);
};

/**
 * The type of the carrier oscillator
 * @memberOf Tone.FMOscillator#
 * @type {string}
 * @name type
 */
Object.defineProperty(Tone.FMOscillator.prototype, "type", {
	get : function(){
		return this._carrier.type;
	},
	set : function(type){
		this._carrier.type = type;
	}
});

/**
 * The oscillator type without the partialsCount appended to the end
 * @memberOf Tone.FMOscillator#
 * @type {string}
 * @name baseType
 * @example
 * osc.type = 'sine2'
 * osc.baseType //'sine'
 * osc.partialCount = 2
 */
Object.defineProperty(Tone.FMOscillator.prototype, "baseType", {
	get : function(){
		return this._carrier.baseType;
	},
	set : function(baseType){
		this._carrier.baseType = baseType;
	}
});

/**
 * 'partialCount' offers an alternative way to set the number of used partials. 
 * When partialCount is 0, the maximum number of partials are used when representing
 * the waveform using the periodicWave. When 'partials' is set, this value is 
 * not settable, but equals the length of the partials array.
 * @memberOf Tone.FMOscillator#
 * @type {Number}
 * @name partialCount
 */
Object.defineProperty(Tone.FMOscillator.prototype, "partialCount", {
	get : function(){
		return this._carrier.partialCount;
	},
	set : function(partialCount){
		this._carrier.partialCount = partialCount;
	}
});

/**
 * The type of the modulator oscillator
 * @memberOf Tone.FMOscillator#
 * @type {String}
 * @name modulationType
 */
Object.defineProperty(Tone.FMOscillator.prototype, "modulationType", {
	get : function(){
		return this._modulator.type;
	},
	set : function(type){
		this._modulator.type = type;
	}
});

/**
 * The phase of the oscillator in degrees.
 * @memberOf Tone.FMOscillator#
 * @type {number}
 * @name phase
 */
Object.defineProperty(Tone.FMOscillator.prototype, "phase", {
	get : function(){
		return this._carrier.phase;
	},
	set : function(phase){
		this._carrier.phase = phase;
		this._modulator.phase = phase;
	}
});

/**
 * The partials of the carrier waveform. A partial represents
 * the amplitude at a harmonic. The first harmonic is the
 * fundamental frequency, the second is the octave and so on
 * following the harmonic series.
 * Setting this value will automatically set the type to "custom".
 * The value is an empty array when the type is not "custom".
 * @memberOf Tone.FMOscillator#
 * @type {Array}
 * @name partials
 * @example
 * osc.partials = [1, 0.2, 0.01];
 */
Object.defineProperty(Tone.FMOscillator.prototype, "partials", {
	get : function(){
		return this._carrier.partials;
	},
	set : function(partials){
		this._carrier.partials = partials;
	}
});

/**
 *  Clean up.
 *  @return {Tone.FMOscillator} this
 */
Tone.FMOscillator.prototype.dispose = function(){
	Tone.Source.prototype.dispose.call(this);
	this._writable(["modulationIndex", "frequency", "detune", "harmonicity"]);
	this.frequency.dispose();
	this.frequency = null;
	this.detune = null;
	this.harmonicity.dispose();
	this.harmonicity = null;
	this._carrier.dispose();
	this._carrier = null;
	this._modulator.dispose();
	this._modulator = null;
	this._modulationNode.dispose();
	this._modulationNode = null;
	this.modulationIndex.dispose();
	this.modulationIndex = null;
	return this;
};

export default Tone.FMOscillator;

import Tone from "../core/Tone";
import "../source/Source";
import "../source/Oscillator";
import "../signal/Signal";
import "../signal/WaveShaper";
import "../core/Gain";

/**
 *  @class Tone.PulseOscillator is a pulse oscillator with control over pulse width,
 *         also known as the duty cycle. At 50% duty cycle (width = 0.5) the wave is
 *         a square and only odd-numbered harmonics are present. At all other widths
 *         even-numbered harmonics are present. Read more
 *         [here](https://wigglewave.wordpress.com/2014/08/16/pulse-waveforms-and-harmonics/).
 *
 *  @constructor
 *  @extends {Tone.Source}
 *  @param {Frequency} [frequency] The frequency of the oscillator
 *  @param {NormalRange} [width] The width of the pulse
 *  @example
 * var pulse = new Tone.PulseOscillator("E5", 0.4).toMaster().start();
 */
Tone.PulseOscillator = function(){

	var options = Tone.defaults(arguments, ["frequency", "width"], Tone.Oscillator);
	Tone.Source.call(this, options);

	/**
	 *  The width of the pulse.
	 *  @type {NormalRange}
	 *  @signal
	 */
	this.width = new Tone.Signal(options.width, Tone.Type.NormalRange);

	/**
	 *  gate the width amount
	 *  @type {Tone.Gain}
	 *  @private
	 */
	this._widthGate = new Tone.Gain(0);

	/**
	 *  the sawtooth oscillator
	 *  @type {Tone.Oscillator}
	 *  @private
	 */
	this._sawtooth = new Tone.Oscillator({
		frequency : options.frequency,
		detune : options.detune,
		type : "sawtooth",
		phase : options.phase
	});

	/**
	 *  The frequency control.
	 *  @type {Frequency}
	 *  @signal
	 */
	this.frequency = this._sawtooth.frequency;

	/**
	 *  The detune in cents.
	 *  @type {Cents}
	 *  @signal
	 */
	this.detune = this._sawtooth.detune;

	/**
	 *  Threshold the signal to turn it into a square
	 *  @type {Tone.WaveShaper}
	 *  @private
	 */
	this._thresh = new Tone.WaveShaper(function(val){
		if (val < 0){
			return -1;
		} else {
			return 1;
		}
	});

	//connections
	this._sawtooth.chain(this._thresh, this.output);
	this.width.chain(this._widthGate, this._thresh);
	this._readOnly(["width", "frequency", "detune"]);
};

Tone.extend(Tone.PulseOscillator, Tone.Source);

/**
 *  The default parameters.
 *  @static
 *  @const
 *  @type {Object}
 */
Tone.PulseOscillator.defaults = {
	"frequency" : 440,
	"detune" : 0,
	"phase" : 0,
	"width" : 0.2,
};

/**
 *  start the oscillator
 *  @param  {Time} time
 *  @private
 */
Tone.PulseOscillator.prototype._start = function(time){
	time = this.toSeconds(time);
	this._sawtooth.start(time);
	this._widthGate.gain.setValueAtTime(1, time);
};

/**
 *  stop the oscillator
 *  @param  {Time} time
 *  @private
 */
Tone.PulseOscillator.prototype._stop = function(time){
	time = this.toSeconds(time);
	this._sawtooth.stop(time);
	//the width is still connected to the output.
	//that needs to be stopped also
	this._widthGate.gain.setValueAtTime(0, time);
};

/**
 *  restart the oscillator
 *  @param  {Time} time (optional) timing parameter
 *  @private
 */
Tone.PulseOscillator.prototype.restart = function(time){
	this._sawtooth.restart(time);
	this._widthGate.gain.cancelScheduledValues(time);
	this._widthGate.gain.setValueAtTime(1, time);
};

/**
 * The phase of the oscillator in degrees.
 * @memberOf Tone.PulseOscillator#
 * @type {Degrees}
 * @name phase
 */
Object.defineProperty(Tone.PulseOscillator.prototype, "phase", {
	get : function(){
		return this._sawtooth.phase;
	},
	set : function(phase){
		this._sawtooth.phase = phase;
	}
});

/**
 * The type of the oscillator. Always returns "pulse".
 * @readOnly
 * @memberOf Tone.PulseOscillator#
 * @type {string}
 * @name type
 */
Object.defineProperty(Tone.PulseOscillator.prototype, "type", {
	get : function(){
		return "pulse";
	}
});

/**
 * The baseType of the oscillator. Always returns "pulse".
 * @readOnly
 * @memberOf Tone.PulseOscillator#
 * @type {string}
 * @name baseType
 */
Object.defineProperty(Tone.PulseOscillator.prototype, "baseType", {
	get : function(){
		return "pulse";
	}
});

/**
 * The partials of the waveform. Cannot set partials for this waveform type
 * @memberOf Tone.PulseOscillator#
 * @type {Array}
 * @name partials
 * @private
 */
Object.defineProperty(Tone.PulseOscillator.prototype, "partials", {
	get : function(){
		return [];
	}
});

/**
 *  Clean up method.
 *  @return {Tone.PulseOscillator} this
 */
Tone.PulseOscillator.prototype.dispose = function(){
	Tone.Source.prototype.dispose.call(this);
	this._sawtooth.dispose();
	this._sawtooth = null;
	this._writable(["width", "frequency", "detune"]);
	this.width.dispose();
	this.width = null;
	this._widthGate.dispose();
	this._widthGate = null;
	this._thresh.dispose();
	this._thresh = null;
	this.frequency = null;
	this.detune = null;
	return this;
};

export default Tone.PulseOscillator;

import Tone from "../core/Tone";
import "../core/Transport";
import "../type/Type";
import "../core/TimelineState";

/**
 *  @class  Tone.Event abstracts away Tone.Transport.schedule and provides a schedulable
 *          callback for a single or repeatable events along the timeline.
 *
 *  @extends {Tone}
 *  @param {function} callback The callback to invoke at the time.
 *  @param {*} value The value or values which should be passed to
 *                      the callback function on invocation.
 *  @example
 * var chord = new Tone.Event(function(time, chord){
 * 	//the chord as well as the exact time of the event
 * 	//are passed in as arguments to the callback function
 * }, ["D4", "E4", "F4"]);
 * //start the chord at the beginning of the transport timeline
 * chord.start();
 * //loop it every measure for 8 measures
 * chord.loop = 8;
 * chord.loopEnd = "1m";
 */
Tone.Event = function(){

	var options = Tone.defaults(arguments, ["callback", "value"], Tone.Event);
	Tone.call(this);

	/**
	 *  Loop value
	 *  @type  {Boolean|Positive}
	 *  @private
	 */
	this._loop = options.loop;

	/**
	 *  The callback to invoke.
	 *  @type  {Function}
	 */
	this.callback = options.callback;

	/**
	 *  The value which is passed to the
	 *  callback function.
	 *  @type  {*}
	 *  @private
	 */
	this.value = options.value;

	/**
	 *  When the note is scheduled to start.
	 *  @type  {Number}
	 *  @private
	 */
	this._loopStart = this.toTicks(options.loopStart);

	/**
	 *  When the note is scheduled to start.
	 *  @type  {Number}
	 *  @private
	 */
	this._loopEnd = this.toTicks(options.loopEnd);

	/**
	 *  Tracks the scheduled events
	 *  @type {Tone.TimelineState}
	 *  @private
	 */
	this._state = new Tone.TimelineState(Tone.State.Stopped);

	/**
	 *  The playback speed of the note. A speed of 1
	 *  is no change.
	 *  @private
	 *  @type {Positive}
	 */
	this._playbackRate = 1;

	/**
	 *  A delay time from when the event is scheduled to start
	 *  @type {Ticks}
	 *  @private
	 */
	this._startOffset = 0;

	/**
	 *  private holder of probability value
	 *  @type {NormalRange}
	 *  @private
	 */
	this._probability = options.probability;

	/**
	 *  the amount of variation from the
	 *  given time.
	 *  @type {Boolean|Time}
	 *  @private
	 */
	this._humanize = options.humanize;

	/**
	 *  If mute is true, the callback won't be
	 *  invoked.
	 *  @type {Boolean}
	 */
	this.mute = options.mute;

	//set the initial values
	this.playbackRate = options.playbackRate;
};

Tone.extend(Tone.Event);

/**
 *  The default values
 *  @type  {Object}
 *  @const
 */
Tone.Event.defaults = {
	"callback" : Tone.noOp,
	"loop" : false,
	"loopEnd" : "1m",
	"loopStart" : 0,
	"playbackRate" : 1,
	"value" : null,
	"probability" : 1,
	"mute" : false,
	"humanize" : false,
};

/**
 *  Reschedule all of the events along the timeline
 *  with the updated values.
 *  @param {Time} after Only reschedules events after the given time.
 *  @return  {Tone.Event}  this
 *  @private
 */
Tone.Event.prototype._rescheduleEvents = function(after){
	//if no argument is given, schedules all of the events
	after = Tone.defaultArg(after, -1);
	this._state.forEachFrom(after, function(event){
		var duration;
		if (event.state === Tone.State.Started){
			if (Tone.isDefined(event.id)){
				Tone.Transport.clear(event.id);
			}
			var startTick = event.time + Math.round(this.startOffset / this._playbackRate);
			if (this._loop === true || Tone.isNumber(this._loop) && this._loop > 1){
				duration = Infinity;
				if (Tone.isNumber(this._loop)){
					duration = (this._loop) * this._getLoopDuration();
				}
				var nextEvent = this._state.getAfter(startTick);
				if (nextEvent !== null){
					duration = Math.min(duration, nextEvent.time - startTick);
				}
				if (duration !== Infinity){
					//schedule a stop since it's finite duration
					this._state.setStateAtTime(Tone.State.Stopped, startTick + duration + 1);
					duration = Tone.Ticks(duration);
				}
				var interval = Tone.Ticks(this._getLoopDuration());
				event.id = Tone.Transport.scheduleRepeat(this._tick.bind(this), interval, Tone.Ticks(startTick), duration);
			} else {
				event.id = Tone.Transport.schedule(this._tick.bind(this), Tone.Ticks(startTick));
			}
		}
	}.bind(this));
	return this;
};

/**
 *  Returns the playback state of the note, either "started" or "stopped".
 *  @type {String}
 *  @readOnly
 *  @memberOf Tone.Event#
 *  @name state
 */
Object.defineProperty(Tone.Event.prototype, "state", {
	get : function(){
		return this._state.getValueAtTime(Tone.Transport.ticks);
	}
});

/**
 *  The start from the scheduled start time
 *  @type {Ticks}
 *  @memberOf Tone.Event#
 *  @name startOffset
 *  @private
 */
Object.defineProperty(Tone.Event.prototype, "startOffset", {
	get : function(){
		return this._startOffset;
	},
	set : function(offset){
		this._startOffset = offset;
	}
});

/**
 *  The probability of the notes being triggered.
 *  @memberOf Tone.Event#
 *  @type {NormalRange}
 *  @name probability
 */
Object.defineProperty(Tone.Event.prototype, "probability", {
	get : function(){
		return this._probability;
	},
	set : function(prob){
		this._probability = prob;
	}
});

/**
 *  If set to true, will apply small random variation
 *  to the callback time. If the value is given as a time, it will randomize
 *  by that amount.
 *  @type {Boolean|Time}
 *  @name humanize
 *  @memberof Tone.Event#
 *  @example
 * event.humanize = true;
 */
Object.defineProperty(Tone.Event.prototype, "humanize", {
	get : function(){
		return this._humanize;
	},
	set : function(variation){
		this._humanize = variation;
	}
});

/**
 *  Start the note at the given time.
 *  @param  {TimelinePosition}  time  When the note should start.
 *  @return  {Tone.Event}  this
 */
Tone.Event.prototype.start = function(time){
	time = this.toTicks(time);
	if (this._state.getValueAtTime(time) === Tone.State.Stopped){
		this._state.add({
			"state" : Tone.State.Started,
			"time" : time,
			"id" : undefined,
		});
		this._rescheduleEvents(time);
	}
	return this;
};

/**
 *  Stop the Event at the given time.
 *  @param  {TimelinePosition}  time  When the note should stop.
 *  @return  {Tone.Event}  this
 */
Tone.Event.prototype.stop = function(time){
	this.cancel(time);
	time = this.toTicks(time);
	if (this._state.getValueAtTime(time) === Tone.State.Started){
		this._state.setStateAtTime(Tone.State.Stopped, time);
		var previousEvent = this._state.getBefore(time);
		var reschedulTime = time;
		if (previousEvent !== null){
			reschedulTime = previousEvent.time;
		}
		this._rescheduleEvents(reschedulTime);
	}
	return this;
};

/**
 *  Cancel all scheduled events greater than or equal to the given time
 *  @param  {TimelinePosition}  [time=0]  The time after which events will be cancel.
 *  @return  {Tone.Event}  this
 */
Tone.Event.prototype.cancel = function(time){
	time = Tone.defaultArg(time, -Infinity);
	time = this.toTicks(time);
	this._state.forEachFrom(time, function(event){
		Tone.Transport.clear(event.id);
	});
	this._state.cancel(time);
	return this;
};

/**
 *  The callback function invoker. Also
 *  checks if the Event is done playing
 *  @param  {Number}  time  The time of the event in seconds
 *  @private
 */
Tone.Event.prototype._tick = function(time){
	var ticks = Tone.Transport.getTicksAtTime(time);
	if (!this.mute && this._state.getValueAtTime(ticks) === Tone.State.Started){
		if (this.probability < 1 && Math.random() > this.probability){
			return;
		}
		if (this.humanize){
			var variation = 0.02;
			if (!Tone.isBoolean(this.humanize)){
				variation = this.toSeconds(this.humanize);
			}
			time += (Math.random() * 2 - 1) * variation;
		}
		this.callback(time, this.value);
	}
};

/**
 *  Get the duration of the loop.
 *  @return  {Ticks}
 *  @private
 */
Tone.Event.prototype._getLoopDuration = function(){
	return Math.round((this._loopEnd - this._loopStart) / this._playbackRate);
};

/**
 *  If the note should loop or not
 *  between Tone.Event.loopStart and
 *  Tone.Event.loopEnd. If set to true,
 *  the event will loop indefinitely,
 *  if set to a number greater than 1
 *  it will play a specific number of
 *  times, if set to false, 0 or 1, the
 *  part will only play once.
 *  @memberOf Tone.Event#
 *  @type {Boolean|Positive}
 *  @name loop
 */
Object.defineProperty(Tone.Event.prototype, "loop", {
	get : function(){
		return this._loop;
	},
	set : function(loop){
		this._loop = loop;
		this._rescheduleEvents();
	}
});

/**
 * 	The playback rate of the note. Defaults to 1.
 *  @memberOf Tone.Event#
 *  @type {Positive}
 *  @name playbackRate
 *  @example
 * note.loop = true;
 * //repeat the note twice as fast
 * note.playbackRate = 2;
 */
Object.defineProperty(Tone.Event.prototype, "playbackRate", {
	get : function(){
		return this._playbackRate;
	},
	set : function(rate){
		this._playbackRate = rate;
		this._rescheduleEvents();
	}
});

/**
 *  The loopEnd point is the time the event will loop
 *  if Tone.Event.loop is true.
 *  @memberOf Tone.Event#
 *  @type {Time}
 *  @name loopEnd
 */
Object.defineProperty(Tone.Event.prototype, "loopEnd", {
	get : function(){
		return Tone.Ticks(this._loopEnd).toSeconds();
	},
	set : function(loopEnd){
		this._loopEnd = this.toTicks(loopEnd);
		if (this._loop){
			this._rescheduleEvents();
		}
	}
});

/**
 *  The time when the loop should start.
 *  @memberOf Tone.Event#
 *  @type {Time}
 *  @name loopStart
 */
Object.defineProperty(Tone.Event.prototype, "loopStart", {
	get : function(){
		return Tone.Ticks(this._loopStart).toSeconds();
	},
