	this._lowpass = this.input = new Tone.Filter({
		"frequency" : options.dampening,
		"type" : "lowpass",
		"Q" : 0,
		"rolloff" : -12
	});

	/**
	 *  The dampening control of the feedback
	 *  @type {Frequency}
	 *  @signal
