	set : function(exp){
		this._exp = exp;
		this._expScaler.setMap(this._expFunc(this._exp));
	}
});

/**
 *  the function which maps the waveshaper
 *  @param   {number} exp
 *  @return {function}
 *  @private
 */
Tone.Pow.prototype._expFunc = function(exp){
	return function(val){
		return Math.pow(Math.abs(val), exp);
