Tone.extend(Tone.Time, Tone.TimeBase);

/**
 * Extend the base expressions
 */
Tone.Time.prototype._expressions = Object.assign({}, Tone.TimeBase.prototype._expressions, {
	"quantize" : {
		regexp : /^@(.+)/,
		method : function(capture){
