/**
 *  Quantize the time by the given subdivision. Optionally add a
 *  percentage which will move the time value towards the ideal
 *  quantized value by that percentage.
 *  @param  {Number|Time}  val    The subdivision to quantize to
 *  @param  {NormalRange}  [percent=1]  Move the time value
 *                                   towards the quantized value by
 *                                   a percentage.
 *  @return  {Number}  this
 *  @example
 * Tone.Time(21).quantize(2) //returns 22
 * Tone.Time(0.6).quantize("4n", 0.5) //returns 0.55
 */
Tone.Time.prototype.quantize = function(subdiv, percent){
	percent = Tone.defaultArg(percent, 1);
	var subdivision = new this.constructor(subdiv);
	var value = this.valueOf();
	var multiple = Math.round(value / subdivision);
	var ideal = multiple * subdivision;
	var diff = ideal - value;
	return value + diff * percent;
};

///////////////////////////////////////////////////////////////////////////
// CONVERSIONS
///////////////////////////////////////////////////////////////////////////

/**
 *  Convert a Time to Notation. The notation values are will be the
 *  closest representation between 1m to 128th note.
 *  @return {Notation}
 *  @example
 * //if the Transport is at 120bpm:
 * Tone.Time(2).toNotation();//returns "1m"
 */
Tone.Time.prototype.toNotation = function(){
	var time = this.toSeconds();
	var testNotations = ["1m"];
	for (var power = 1; power < 8; power++){
		var subdiv = Math.pow(2, power);
		testNotations.push(subdiv + "n.");
		testNotations.push(subdiv + "n");
		testNotations.push(subdiv + "t");
	}
	testNotations.push("0");
	//find the closets notation representation
	var closest = testNotations[0];
	var closestSeconds = Tone.Time(testNotations[0]).toSeconds();
	testNotations.forEach(function(notation){
		var notationSeconds = Tone.Time(notation).toSeconds();
		if (Math.abs(notationSeconds - time) < Math.abs(closestSeconds - time)){
			closest = notation;
			closestSeconds = notationSeconds;
		}
	});
	return closest;
};

/**
 *  Return the time encoded as Bars:Beats:Sixteenths.
 *  @return  {BarsBeatsSixteenths}
 */
Tone.Time.prototype.toBarsBeatsSixteenths = function(){
	var quarterTime = this._beatsToUnits(1);
	var quarters = this.valueOf() / quarterTime;
	quarters = parseFloat(quarters.toFixed(4));
	var measures = Math.floor(quarters / this._getTimeSignature());
	var sixteenths = (quarters % 1) * 4;
	quarters = Math.floor(quarters) % this._getTimeSignature();
	sixteenths = sixteenths.toString();
	if (sixteenths.length > 3){
		// the additional parseFloat removes insignificant trailing zeroes
		sixteenths = parseFloat(parseFloat(sixteenths).toFixed(3));
	}
	var progress = [measures, quarters, sixteenths];
	return progress.join(":");
};

/**
 *  Return the time in ticks.
 *  @return  {Ticks}
 */
Tone.Time.prototype.toTicks = function(){
	var quarterTime = this._beatsToUnits(1);
	var quarters = this.valueOf() / quarterTime;
	return Math.round(quarters * this._getPPQ());
};

/**
 *  Return the time in seconds.
 *  @return  {Seconds}
 */
Tone.Time.prototype.toSeconds = function(){
	return this.valueOf();
};

/**
 *  Return the value as a midi note.
 *  @return  {Midi}
 */
Tone.Time.prototype.toMidi = function(){
	return Tone.Frequency.ftom(this.toFrequency());
};

export default Tone.Time;

import Tone from "../core/Tone";

if (Tone.supported){
	if (!OscillatorNode.prototype.setPeriodicWave){
		OscillatorNode.prototype.setPeriodicWave = OscillatorNode.prototype.setWaveTable;
	}
	if (!AudioContext.prototype.createPeriodicWave){
		AudioContext.prototype.createPeriodicWave = AudioContext.prototype.createWaveTable;
	}
}

import Tone from "../core/Tone";
import "../core/Buffer";
import "../source/Source";
import "../core/Gain";
import "../core/AudioNode";
import "../shim/OscillatorNode";

/**
 *  @class Wrapper around the native fire-and-forget OscillatorNode. Adds the
 *     ability to reschedule the stop method. ***[Tone.Oscillator](Oscillator) is better 
 *     for most use-cases***
 *  @extends {Tone.AudioNode}
 *  @param  {AudioBuffer|Tone.Buffer}  buffer   The buffer to play
 *  @param  {Function}  onload  The callback to invoke when the
 *                               buffer is done playing.
 */
Tone.OscillatorNode = function(){

	var options = Tone.defaults(arguments, ["frequency", "type"], Tone.OscillatorNode);
	Tone.AudioNode.call(this, options);

	/**
	 *  The callback to invoke after the
	 *  buffer source is done playing.
	 *  @type  {Function}
	 */
	this.onended = options.onended;

	/**
	 *  The oscillator start time
	 *  @type  {Number}
	 *  @private
	 */
	this._startTime = -1;

	/**
	 *  The oscillator stop time
	 *  @type  {Number}
	 *  @private
	 */
	this._stopTime = -1;

	/**
	 *  The gain node which envelopes the OscillatorNode
	 *  @type  {Tone.Gain}
	 *  @private
	 */
	this._gainNode = this.output = new Tone.Gain(0);

	/**
	 *  The oscillator
	 *  @type  {OscillatorNode}
	 *  @private
	 */
	this._oscillator = this.context.createOscillator();
	Tone.connect(this._oscillator, this._gainNode);
	this.type = options.type;

	/**
	 *  The frequency of the oscillator
	 *  @type {Frequency}
	 *  @signal
	 */
	this.frequency = new Tone.Param({
		param : this._oscillator.frequency, 
		units : Tone.Type.Frequency,
		value : options.frequency
	});

	/**
	 *  The detune of the oscillator
	 *  @type {Frequency}
	 *  @signal
	 */
	this.detune = new Tone.Param({
		param : this._oscillator.detune,
		units : Tone.Type.Cents,
		value : options.detune
	});

	/**
	 *  The value that the buffer ramps to
	 *  @type {Gain}
	 *  @private
	 */
	this._gain = 1;
};

Tone.extend(Tone.OscillatorNode, Tone.AudioNode);

/**
 *  The defaults
 *  @const
 *  @type  {Object}
 */
Tone.OscillatorNode.defaults = {
	"frequency" : 440,
	"detune" : 0,
	"type" : "sine",
	"onended" : Tone.noOp
};

/**
 *  Returns the playback state of the oscillator, either "started" or "stopped".
 *  @type {Tone.State}
 *  @readOnly
 *  @memberOf Tone.OscillatorNode#
 *  @name state
 */
Object.defineProperty(Tone.OscillatorNode.prototype, "state", {
	get : function(){
		return this.getStateAtTime(this.now());
	}
});

/**
 *  Get the playback state at the given time
 *  @param  {Time}  time  The time to test the state at
 *  @return  {Tone.State}  The playback state. 
 */
Tone.OscillatorNode.prototype.getStateAtTime = function(time){
	time = this.toSeconds(time);
	if (this._startTime !== -1 && time >= this._startTime && (this._stopTime === -1 || time <= this._stopTime)){
		return Tone.State.Started;
	} else {
		return Tone.State.Stopped;
	}
};

/**
     * Start the oscillator node at the given time
     * @param  {Time=} time When to start the oscillator
     * @return {OscillatorNode}      this
     */
Tone.OscillatorNode.prototype.start = function(time){
	this.log("start", time);
	if (this._startTime === -1){
		this._startTime = this.toSeconds(time);
		this._startTime = Math.max(this._startTime, this.context.currentTime);
		this._oscillator.start(this._startTime);
		this._gainNode.gain.setValueAtTime(1, this._startTime);
	} else {
		throw new Error("cannot call OscillatorNode.start more than once");
	}
	return this;
};

/**
     * Sets an arbitrary custom periodic waveform given a PeriodicWave.
     * @param  {PeriodicWave} periodicWave PeriodicWave should be created with context.createPeriodicWave
     * @return {OscillatorNode} this
     */
Tone.OscillatorNode.prototype.setPeriodicWave = function(periodicWave){
	this._oscillator.setPeriodicWave(periodicWave);
	return this;
};

/**
     * Stop the oscillator node at the given time
     * @param  {Time=} time When to stop the oscillator
     * @return {OscillatorNode}      this
     */
Tone.OscillatorNode.prototype.stop = function(time){
	this.log("stop", time);
	this.assert(this._startTime !== -1, "'start' must be called before 'stop'");
	//cancel the previous stop
	this.cancelStop();
	//reschedule it
	this._stopTime = this.toSeconds(time);
	this._stopTime = Math.max(this._stopTime, this.context.currentTime);
	if (this._stopTime > this._startTime){
		this._gainNode.gain.setValueAtTime(0, this._stopTime);
		this.context.clearTimeout(this._timeout);
		this._timeout = this.context.setTimeout(function(){
			this._oscillator.stop(this.now());
			this.onended();
			//disconnect the object when it's ended
			setTimeout(function(){
				if (this._oscillator){
					this._oscillator.disconnect();
					this._gainNode.disconnect();
				}
			}.bind(this), 100);
		}.bind(this), this._stopTime - this.context.currentTime);
	} else {
		//cancel the stop envelope
		this._gainNode.gain.cancelScheduledValues(this._startTime);
	}
	return this;
};

/**
 *  Cancel a scheduled stop event
 *  @return  {Tone.OscillatorNode}  this
 */
Tone.OscillatorNode.prototype.cancelStop = function(){
	if (this._startTime !== -1){
		//cancel the stop envelope
		this._gainNode.gain.cancelScheduledValues(this._startTime+this.sampleTime);
		this.context.clearTimeout(this._timeout);
		this._stopTime = -1;
	}
	return this;
};

/**
 * The oscillator type. Either 'sine', 'sawtooth', 'square', or 'triangle'
 * @memberOf Tone.OscillatorNode#
 * @type {Time}
 * @name type
 */
Object.defineProperty(Tone.OscillatorNode.prototype, "type", {
	get : function(){
		return this._oscillator.type;
	},
	set : function(type){
		this._oscillator.type = type;
	}
});

/**
 *  Clean up.
 *  @return  {Tone.OscillatorNode}  this
 */
Tone.OscillatorNode.prototype.dispose = function(){
	if (!this._wasDisposed){
		this._wasDisposed = true;
		this.context.clearTimeout(this._timeout);
		Tone.AudioNode.prototype.dispose.call(this);
		this.onended = null;
		this._oscillator.disconnect();
		this._oscillator = null;
		this._gainNode.dispose();
		this._gainNode = null;
		this.frequency.dispose();
		this.frequency = null;
		this.detune.dispose();
		this.detune = null;
	}
	return this;
};

export default Tone.OscillatorNode;

import Tone from "../core/Tone";
import "../core/Buffer";
import "../source/Source";
import "../source/TickSource";
import "../source/BufferSource";

/**
 *  @class  Tone.Player is an audio file player with start, loop, and stop functions.
 *
 *  @constructor
 *  @extends {Tone.Source}
 *  @param {string|AudioBuffer} url Either the AudioBuffer or the url from
 *                                  which to load the AudioBuffer
 *  @param {Function=} onload The function to invoke when the buffer is loaded.
 *                            Recommended to use Tone.Buffer.on('load') instead.
 *  @example
 * var player = new Tone.Player("./path/to/sample.mp3").toMaster();
 * //play as soon as the buffer is loaded
 * player.autostart = true;
 */
Tone.Player = function(url){

	var options;
	if (url instanceof Tone.Buffer && url.loaded){
		url = url.get();
		options = Tone.Player.defaults;
	} else {
		options = Tone.defaults(arguments, ["url", "onload"], Tone.Player);
	}
	Tone.Source.call(this, options);

	/**
	 *  If the file should play as soon
	 *  as the buffer is loaded.
	 *  @type {Boolean}
	 *  @example
	 * //will play as soon as it's loaded
	 * var player = new Tone.Player({
	 * 	"url" : "./path/to/sample.mp3",
	 * 	"autostart" : true,
	 * }).toMaster();
	 */
	this.autostart = options.autostart;

	/**
	 *  the buffer
	 *  @private
	 *  @type {Tone.Buffer}
	 */
	this._buffer = new Tone.Buffer({
		"url" : options.url,
		"onload" : this._onload.bind(this, options.onload),
		"reverse" : options.reverse
	});
	if (url instanceof AudioBuffer){
		this._buffer.set(url);
	}

	/**
	 *  if the buffer should loop once it's over
	 *  @type {Boolean}
	 *  @private
	 */
	this._loop = options.loop;

	/**
	 *  if 'loop' is true, the loop will start at this position
	 *  @type {Time}
	 *  @private
	 */
	this._loopStart = options.loopStart;

	/**
	 *  if 'loop' is true, the loop will end at this position
	 *  @type {Time}
	 *  @private
	 */
	this._loopEnd = options.loopEnd;

	/**
	 *  the playback rate
	 *  @private
	 *  @type {Number}
	 */
	this._playbackRate = options.playbackRate;

	/**
	 *  All of the active buffer source nodes
	 *  @type {Array<Tone.BufferSource>}
	 *  @private
	 */
	this._activeSources = [];

	/**
	 *  The fadeIn time of the amplitude envelope.
	 *  @type {Time}
	 */
	this.fadeIn = options.fadeIn;

	/**
	 *  The fadeOut time of the amplitude envelope.
	 *  @type {Time}
	 */
	this.fadeOut = options.fadeOut;
};

Tone.extend(Tone.Player, Tone.Source);

/**
 *  the default parameters
 *  @static
 *  @const
 *  @type {Object}
 */
Tone.Player.defaults = {
	"onload" : Tone.noOp,
	"playbackRate" : 1,
	"loop" : false,
	"autostart" : false,
	"loopStart" : 0,
	"loopEnd" : 0,
	"reverse" : false,
	"fadeIn" : 0,
	"fadeOut" : 0
};

/**
 *  Load the audio file as an audio buffer.
 *  Decodes the audio asynchronously and invokes
 *  the callback once the audio buffer loads.
 *  Note: this does not need to be called if a url
 *  was passed in to the constructor. Only use this
 *  if you want to manually load a new url.
 * @param {string} url The url of the buffer to load.
 *                     Filetype support depends on the
 *                     browser.
 *  @param  {Function=} callback The function to invoke once
 *                               the sample is loaded.
 *  @returns {Promise}
 */
Tone.Player.prototype.load = function(url, callback){
	return this._buffer.load(url, this._onload.bind(this, callback));
};

/**
 * Internal callback when the buffer is loaded.
 * @private
 */
Tone.Player.prototype._onload = function(callback){
	callback = Tone.defaultArg(callback, Tone.noOp);
	callback(this);
	if (this.autostart){
		this.start();
	}
};

/**
 * Internal callback when the buffer is done playing.
 * @private
 */
Tone.Player.prototype._onSourceEnd = function(source){
	var index = this._activeSources.indexOf(source);
	this._activeSources.splice(index, 1);
	if (this._activeSources.length === 0 && !this._synced){
		this._state.setStateAtTime(Tone.State.Stopped, Tone.now());
	}
};

/**
 *  Play the buffer at the given startTime. Optionally add an offset
 *  and/or duration which will play the buffer from a position
 *  within the buffer for the given duration.
 *
 *  @param  {Time} [startTime=now] When the player should start.
 *  @param  {Time} [offset=0] The offset from the beginning of the sample
 *                                 to start at.
 *  @param  {Time=} duration How long the sample should play. If no duration
 *                                is given, it will default to the full length
 *                                of the sample (minus any offset)
 *  @returns {Tone.Player} this
 *  @memberOf Tone.Player#
 *  @method start
 *  @name start
 */

/**
 *  Internal start method
 *  @private
 */
Tone.Player.prototype._start = function(startTime, offset, duration){
	//if it's a loop the default offset is the loopstart point
	if (this._loop){
		offset = Tone.defaultArg(offset, this._loopStart);
	} else {
		//otherwise the default offset is 0
		offset = Tone.defaultArg(offset, 0);
	}

	//compute the values in seconds
	offset = this.toSeconds(offset);

	//if it's synced, it should factor in the playback rate for computing the offset
	if (this._synced){
		offset *= this._playbackRate;
	}

	//compute the duration which is either the passed in duration of the buffer.duration - offset
	var computedDuration = Tone.defaultArg(duration, Math.max(this._buffer.duration - offset, 0));
	computedDuration = this.toSeconds(computedDuration);

	//scale it by the playback rate
	computedDuration = computedDuration / this._playbackRate;

	//get the start time
	startTime = this.toSeconds(startTime);

	//make the source
	var source = new Tone.BufferSource({
		"buffer" : this._buffer,
		"loop" : this._loop,
		"loopStart" : this._loopStart,
		"loopEnd" : this._loopEnd,
		"onended" : this._onSourceEnd.bind(this),
		"playbackRate" : this._playbackRate,
		"fadeIn" : this.fadeIn,
		"fadeOut" : this.fadeOut,
	}).connect(this.output);

	//set the looping properties
	if (!this._loop && !this._synced){
		//if it's not looping, set the state change at the end of the sample
		this._state.setStateAtTime(Tone.State.Stopped, startTime + computedDuration);
	}

	//add it to the array of active sources
	this._activeSources.push(source);

	//start it
	if (this._loop && Tone.isUndef(duration)){
		source.start(startTime, offset);
	} else {
		//subtract the fade out time
		source.start(startTime, offset, computedDuration - this.toSeconds(this.fadeOut));
	}
	return this;
};

/**
 *  Stop playback.
 *  @private
 *  @param  {Time} [time=now]
 *  @returns {Tone.Player} this
 */
Tone.Player.prototype._stop = function(time){
	time = this.toSeconds(time);
	this._activeSources.forEach(function(source){
		source.stop(time);
	});
	return this;
};

/**
 * Stop and then restart the player from the beginning (or offset)
 *  @param  {Time} [startTime=now] When the player should start.
 *  @param  {Time} [offset=0] The offset from the beginning of the sample
 *                                 to start at.
 *  @param  {Time=} duration How long the sample should play. If no duration
 *                                is given, it will default to the full length
 *                                of the sample (minus any offset)
 *  @returns {Tone.Player} this
 */
Tone.Player.prototype.restart = function(time, offset, duration){
	this._stop(time);
	this._start(time, offset, duration);
	return this;
};

/**
 *  Seek to a specific time in the player's buffer. If the
 *  source is no longer playing at that time, it will stop.
 *  If you seek to a time that
 *  @param {Time} offset The time to seek to.
 *  @param {Time=} time The time for the seek event to occur.
 *  @return {Tone.Player} this
 *  @example
 * source.start(0.2);
 * source.stop(0.4);
 */
Tone.Player.prototype.seek = function(offset, time){
	time = this.toSeconds(time);
	if (this._state.getValueAtTime(time) === Tone.State.Started){
		offset = this.toSeconds(offset);
		// if it's currently playing, stop it
		this._stop(time);
		//restart it at the given time
		this._start(time, offset);
	}
	return this;
};

/**
 *  Set the loop start and end. Will only loop if loop is
 *  set to true.
 *  @param {Time} loopStart The loop end time
 *  @param {Time} loopEnd The loop end time
 *  @returns {Tone.Player} this
 *  @example
 * //loop 0.1 seconds of the file.
 * player.setLoopPoints(0.2, 0.3);
 * player.loop = true;
 */
Tone.Player.prototype.setLoopPoints = function(loopStart, loopEnd){
	this.loopStart = loopStart;
	this.loopEnd = loopEnd;
	return this;
};

/**
 * If loop is true, the loop will start at this position.
 * @memberOf Tone.Player#
 * @type {Time}
 * @name loopStart
 */
Object.defineProperty(Tone.Player.prototype, "loopStart", {
	get : function(){
		return this._loopStart;
	},
	set : function(loopStart){
		this._loopStart = loopStart;
		//get the current source
		this._activeSources.forEach(function(source){
			source.loopStart = loopStart;
		});
	}
});

/**
 * If loop is true, the loop will end at this position.
 * @memberOf Tone.Player#
 * @type {Time}
 * @name loopEnd
 */
Object.defineProperty(Tone.Player.prototype, "loopEnd", {
	get : function(){
		return this._loopEnd;
	},
	set : function(loopEnd){
		this._loopEnd = loopEnd;
		//get the current source
		this._activeSources.forEach(function(source){
			source.loopEnd = loopEnd;
		});
	}
});

/**
 * The audio buffer belonging to the player.
 * @memberOf Tone.Player#
 * @type {Tone.Buffer}
 * @name buffer
 */
Object.defineProperty(Tone.Player.prototype, "buffer", {
	get : function(){
		return this._buffer;
	},
	set : function(buffer){
		this._buffer.set(buffer);
	}
});

/**
 * If the buffer should loop once it's over.
 * @memberOf Tone.Player#
 * @type {Boolean}
 * @name loop
 */
Object.defineProperty(Tone.Player.prototype, "loop", {
	get : function(){
		return this._loop;
	},
	set : function(loop){
		//if no change, do nothing
		if (this._loop === loop){
			return;
		}
		this._loop = loop;
		//set the loop of all of the sources
		this._activeSources.forEach(function(source){
			source.loop = loop;
		});
		if (loop){
			//remove the next stopEvent
			var stopEvent = this._state.getNextState(Tone.State.Stopped, this.now());
			if (stopEvent){
				this._state.cancel(stopEvent.time);
			}
		}
	}
});

/**
 * The playback speed. 1 is normal speed. This is not a signal because
 * Safari and iOS currently don't support playbackRate as a signal.
 * @memberOf Tone.Player#
 * @type {Number}
 * @name playbackRate
 */
Object.defineProperty(Tone.Player.prototype, "playbackRate", {
	get : function(){
		return this._playbackRate;
	},
	set : function(rate){
		this._playbackRate = rate;
		var now = this.now();

		//cancel the stop event since it's at a different time now
		var stopEvent = this._state.getNextState(Tone.State.Stopped, now);
		if (stopEvent){
			this._state.cancel(stopEvent.time);
		}

		//set all the sources
		this._activeSources.forEach(function(source){
			source.cancelStop();
			source.playbackRate.setValueAtTime(rate, now);
		});
	}
});

/**
 * The direction the buffer should play in
 * @memberOf Tone.Player#
 * @type {Boolean}
 * @name reverse
 */
Object.defineProperty(Tone.Player.prototype, "reverse", {
	get : function(){
		return this._buffer.reverse;
	},
	set : function(rev){
		this._buffer.reverse = rev;
	}
});

/**
 * If all the buffer is loaded
 * @memberOf Tone.Player#
 * @type {Boolean}
 * @name loaded
 * @readOnly
 */
Object.defineProperty(Tone.Player.prototype, "loaded", {
	get : function(){
		return this._buffer.loaded;
	}
});

/**
 *  Dispose and disconnect.
 *  @return {Tone.Player} this
 */
Tone.Player.prototype.dispose = function(){
	//disconnect all of the players
	this._activeSources.forEach(function(source){
		source.dispose();
	});
	this._activeSources = null;
	Tone.Source.prototype.dispose.call(this);
	this._buffer.dispose();
	this._buffer = null;
	return this;
};

export default Tone.Player;

import Tone from "../core/Tone";
import "../component/AmplitudeEnvelope";
import "../component/FrequencyEnvelope";
import "../source/OmniOscillator";
import "../signal/Signal";
import "../component/Filter";
import "../instrument/Monophonic";

/**
 *  @class  Tone.MonoSynth is composed of one oscillator, one filter, and two envelopes.
 *          The amplitude of the Tone.Oscillator and the cutoff frequency of the
 *          Tone.Filter are controlled by Tone.Envelopes.
 *          <img src="https://docs.google.com/drawings/d/1gaY1DF9_Hzkodqf8JI1Cg2VZfwSElpFQfI94IQwad38/pub?w=924&h=240">
 *
 *  @constructor
 *  @extends {Tone.Monophonic}
 *  @param {Object} [options] the options available for the synth
 *                          see defaults below
 *  @example
 * var synth = new Tone.MonoSynth({
 * 	"oscillator" : {
 * 		"type" : "square"
 *  },
 *  "envelope" : {
 *  	"attack" : 0.1
 *  }
 * }).toMaster();
 * synth.triggerAttackRelease("C4", "8n");
 */
Tone.MonoSynth = function(options){

	//get the defaults
	options = Tone.defaultArg(options, Tone.MonoSynth.defaults);
	Tone.Monophonic.call(this, options);

	/**
	 *  The oscillator.
	 *  @type {Tone.OmniOscillator}
	 */
	this.oscillator = new Tone.OmniOscillator(options.oscillator);

	/**
	 *  The frequency control.
	 *  @type {Frequency}
	 *  @signal
	 */
	this.frequency = this.oscillator.frequency;

	/**
	 *  The detune control.
	 *  @type {Cents}
	 *  @signal
	 */
	this.detune = this.oscillator.detune;

	/**
	 *  The filter.
	 *  @type {Tone.Filter}
	 */
	this.filter = new Tone.Filter(options.filter);
	this.filter.frequency.value = 5000;

	/**
	 *  The filter envelope.
	 *  @type {Tone.FrequencyEnvelope}
	 */
	this.filterEnvelope = new Tone.FrequencyEnvelope(options.filterEnvelope);

	/**
	 *  The amplitude envelope.
	 *  @type {Tone.AmplitudeEnvelope}
	 */
	this.envelope = new Tone.AmplitudeEnvelope(options.envelope);

	//connect the oscillators to the output
	this.oscillator.chain(this.filter, this.envelope, this.output);
	//connect the filter envelope
	this.filterEnvelope.connect(this.filter.frequency);
	this._readOnly(["oscillator", "frequency", "detune", "filter", "filterEnvelope", "envelope"]);
};

Tone.extend(Tone.MonoSynth, Tone.Monophonic);

/**
 *  @const
 *  @static
 *  @type {Object}
 */
Tone.MonoSynth.defaults = {
	"frequency" : "C4",
	"detune" : 0,
	"oscillator" : {
		"type" : "square"
	},
	"filter" : {
		"Q" : 6,
		"type" : "lowpass",
		"rolloff" : -24
	},
	"envelope" : {
		"attack" : 0.005,
		"decay" : 0.1,
		"sustain" : 0.9,
		"release" : 1
	},
	"filterEnvelope" : {
		"attack" : 0.06,
		"decay" : 0.2,
		"sustain" : 0.5,
		"release" : 2,
		"baseFrequency" : 200,
		"octaves" : 7,
		"exponent" : 2
	}
};

/**
 *  start the attack portion of the envelope
 *  @param {Time} [time=now] the time the attack should start
 *  @param {NormalRange} [velocity=1] the velocity of the note (0-1)
 *  @returns {Tone.MonoSynth} this
 *  @private
 */
Tone.MonoSynth.prototype._triggerEnvelopeAttack = function(time, velocity){
	time = this.toSeconds(time);
	//the envelopes
	this.envelope.triggerAttack(time, velocity);
	this.filterEnvelope.triggerAttack(time);
	this.oscillator.start(time);
	if (this.envelope.sustain === 0){
		this.oscillator.stop(time + this.envelope.attack + this.envelope.decay);
	}
	return this;
};

/**
 *  start the release portion of the envelope
 *  @param {Time} [time=now] the time the release should start
 *  @returns {Tone.MonoSynth} this
 *  @private
 */
Tone.MonoSynth.prototype._triggerEnvelopeRelease = function(time){
	this.envelope.triggerRelease(time);
	this.filterEnvelope.triggerRelease(time);
	this.oscillator.stop(time + this.envelope.release);
	return this;
};

/**
 *  clean up
 *  @returns {Tone.MonoSynth} this
 */
Tone.MonoSynth.prototype.dispose = function(){
	Tone.Monophonic.prototype.dispose.call(this);
	this._writable(["oscillator", "frequency", "detune", "filter", "filterEnvelope", "envelope"]);
	this.oscillator.dispose();
	this.oscillator = null;
	this.envelope.dispose();
	this.envelope = null;
	this.filterEnvelope.dispose();
	this.filterEnvelope = null;
	this.filter.dispose();
	this.filter = null;
	this.frequency = null;
	this.detune = null;
	return this;
};

export default Tone.MonoSynth;

import Tone from "../core/Tone";
import "../source/Source";
import "../source/Oscillator";
