	this.sideSend = this._midSideSplit.side;

	/**
	 *  The mid return connection
	 *  @type {GainNode}
	 *  @private
	 */
	this.midReturn = this._midSideMerge.mid;

	/**
	 *  The side return connection
	 *  @type {GainNode}
	 *  @private
	 */
	this.sideReturn = this._midSideMerge.side;

