import Tone from "../core/Tone";
import "../component/LFO";
import "../effect/StereoEffect";
import "../core/Delay";

/**
 *  @class Tone.Chorus is a stereo chorus effect composed of
 *         a left and right delay with a Tone.LFO applied to the delayTime of each channel.
 *         Inspiration from [Tuna.js](https://github.com/Dinahmoe/tuna/blob/master/tuna.js).
 *         Read more on the chorus effect on [SoundOnSound](http://www.soundonsound.com/sos/jun04/articles/synthsecrets.htm).
 *
 *	@constructor
 *	@extends {Tone.StereoEffect}
 *	@param {Frequency|Object} [frequency] The frequency of the LFO.
 *	@param {Milliseconds} [delayTime] The delay of the chorus effect in ms.
 *	@param {NormalRange} [depth] The depth of the chorus.
 *	@example
 * var chorus = new Tone.Chorus(4, 2.5, 0.5);
 * var synth = new Tone.PolySynth(4, Tone.MonoSynth).connect(chorus);
 * synth.triggerAttackRelease(["C3","E3","G3"], "8n");
 */
Tone.Chorus = function(){

	var options = Tone.defaults(arguments, ["frequency", "delayTime", "depth"], Tone.Chorus);
	Tone.StereoEffect.call(this, options);

	/**
	 *  the depth of the chorus
	 *  @type {number}
	 *  @private
	 */
	this._depth = options.depth;

	/**
	 *  the delayTime
	 *  @type {number}
	 *  @private
	 */
	this._delayTime = options.delayTime / 1000;

	/**
	 *  the lfo which controls the delayTime
	 *  @type {Tone.LFO}
	 *  @private
	 */
	this._lfoL = new Tone.LFO({
		"frequency" : options.frequency,
		"min" : 0,
		"max" : 1,
	});

	/**
	 *  another LFO for the right side with a 180 degree phase diff
	 *  @type {Tone.LFO}
	 *  @private
	 */
	this._lfoR = new Tone.LFO({
		"frequency" : options.frequency,
		"min" : 0,
		"max" : 1,
		"phase" : 180
	});

	/**
	 *  delay for left
	 *  @type {Tone.Delay}
	 *  @private
	 */
	this._delayNodeL = new Tone.Delay();

	/**
	 *  delay for right
	 *  @type {Tone.Delay}
	 *  @private
	 */
	this._delayNodeR = new Tone.Delay();

	/**
	 * The frequency of the LFO which modulates the delayTime.
	 * @type {Frequency}
	 * @signal
	 */
	this.frequency = this._lfoL.frequency;

	//connections
	this.effectSendL.chain(this._delayNodeL, this.effectReturnL);
	this.effectSendR.chain(this._delayNodeR, this.effectReturnR);
	//and pass through to make the detune apparent
	this.effectSendL.connect(this.effectReturnL);
	this.effectSendR.connect(this.effectReturnR);
	//lfo setup
	this._lfoL.connect(this._delayNodeL.delayTime);
	this._lfoR.connect(this._delayNodeR.delayTime);
	//start the lfo
	this._lfoL.start();
	this._lfoR.start();
	//have one LFO frequency control the other
	this._lfoL.frequency.connect(this._lfoR.frequency);
	//set the initial values
	this.depth = this._depth;
	this.frequency.value = options.frequency;
	this.type = options.type;
	this._readOnly(["frequency"]);
	this.spread = options.spread;
};

Tone.extend(Tone.Chorus, Tone.StereoEffect);

/**
 *  @static
 *  @type {Object}
 */
Tone.Chorus.defaults = {
	"frequency" : 1.5,
	"delayTime" : 3.5,
	"depth" : 0.7,
	"type" : "sine",
	"spread" : 180
};

/**
 * The depth of the effect. A depth of 1 makes the delayTime
 * modulate between 0 and 2*delayTime (centered around the delayTime).
 * @memberOf Tone.Chorus#
 * @type {NormalRange}
 * @name depth
 */
