	"oversample" : "none"
};

/**
 *  get the coefficient for that degree
 *  @param {number} x the x value
 *  @param   {number} degree 
 *  @param {Object} memo memoize the computed value. 
 *                       this speeds up computation greatly. 
 *  @return  {number}       the coefficient 
 *  @private
 */
Tone.Chebyshev.prototype._getCoefficient = function(x, degree, memo){
	if (memo.hasOwnProperty(degree)){
		return memo[degree];
	} else if (degree === 0){
		memo[degree] = 0;
	} else if (degree === 1){
		memo[degree] = x;
	} else {
		memo[degree] = 2 * x * this._getCoefficient(x, degree - 1, memo) - this._getCoefficient(x, degree - 2, memo);
	}
	return memo[degree];
};

/**
 * The order of the Chebyshev polynomial which creates
 * the equation which is applied to the incoming 
 * signal through a Tone.WaveShaper. The equations
 * are in the form:<br>
 * order 2: 2x^2 + 1<br>
 * order 3: 4x^3 + 3x <br>
 * @memberOf Tone.Chebyshev#
 * @type {Positive}
 * @name order
 */
Object.defineProperty(Tone.Chebyshev.prototype, "order", {
	get : function(){
		return this._order;
	},
	set : function(order){
		this._order = order;
		var curve = new Array(4096);
		var len = curve.length;
		for (var i = 0; i < len; ++i){
			var x = i * 2 / len - 1;
			if (x === 0){
				//should output 0 when input is 0
				curve[i] = 0;
			} else {
				curve[i] = this._getCoefficient(x, order, {});
			}
		}
		this._shaper.curve = curve;
	} 
});

/**
 * The oversampling of the effect. Can either be "none", "2x" or "4x".
 * @memberOf Tone.Chebyshev#
 * @type {string}
 * @name oversample
 */
Object.defineProperty(Tone.Chebyshev.prototype, "oversample", {
	get : function(){
		return this._shaper.oversample;
	},
	set : function(oversampling){
		this._shaper.oversample = oversampling;
	} 
});

/**
 *  Clean up. 
 *  @returns {Tone.Chebyshev} this
 */
Tone.Chebyshev.prototype.dispose = function(){
	Tone.Effect.prototype.dispose.call(this);
	this._shaper.dispose();
	this._shaper = null;
	return this;
};

export default Tone.Chebyshev;

import Tone from "../core/Tone";
import "../effect/Effect";
import "../signal/Subtract";
import "../signal/Modulo";

/**
 *  @class Tone.Bitcrusher downsamples the incoming signal to a different bitdepth.
 *         Lowering the bitdepth of the signal creates distortion. Read more about Bitcrushing
 *         on [Wikipedia](https://en.wikipedia.org/wiki/Bitcrusher).
 *
 *  @constructor
 *  @extends {Tone.Effect}
 *  @param {Number} bits The number of bits to downsample the signal. Nominal range
 *                       of 1 to 8.
 *  @example
 * //initialize crusher and route a synth through it
 * var crusher = new Tone.BitCrusher(4).toMaster();
 * var synth = new Tone.MonoSynth().connect(crusher);
 */
Tone.BitCrusher = function(){

	var options = Tone.defaults(arguments, ["bits"], Tone.BitCrusher);
	Tone.Effect.call(this, options);

	var invStepSize = 1 / Math.pow(2, options.bits - 1);

	/**
	 *  Subtract the input signal and the modulus of the input signal
	 *  @type {Tone.Subtract}
	 *  @private
	 */
	this._subtract = new Tone.Subtract();

	/**
	 *  The mod function
	 *  @type  {Tone.Modulo}
	 *  @private
	 */
	this._modulo = new Tone.Modulo(invStepSize);

	/**
	 *  keeps track of the bits
	 *  @type {number}
	 *  @private
	 */
	this._bits = options.bits;

	//connect it up
	this.effectSend.fan(this._subtract, this._modulo);
	this._modulo.connect(this._subtract, 0, 1);
	this._subtract.connect(this.effectReturn);
};

Tone.extend(Tone.BitCrusher, Tone.Effect);

/**
 *  the default values
 *  @static
 *  @type {Object}
 */
Tone.BitCrusher.defaults = {
	"bits" : 4
};

/**
 * The bit depth of the effect. Nominal range of 1-8.
 * @memberOf Tone.BitCrusher#
 * @type {number}
 * @name bits
 */
Object.defineProperty(Tone.BitCrusher.prototype, "bits", {
	get : function(){
		return this._bits;
	},
	set : function(bits){
		this._bits = bits;
		var invStepSize = 1 / Math.pow(2, bits - 1);
		this._modulo.value = invStepSize;
	}
});

/**
 *  Clean up.
 *  @returns {Tone.BitCrusher} this
 */
Tone.BitCrusher.prototype.dispose = function(){
	Tone.Effect.prototype.dispose.call(this);
	this._subtract.dispose();
	this._subtract = null;
	this._modulo.dispose();
	this._modulo = null;
	return this;
};

export default Tone.BitCrusher;

import Tone from "../core/Tone";
import "../component/Follower";
import "../signal/ScaleExp";
import "../effect/Effect";
import "../component/Filter";

/**
 *  @class  Tone.AutoWah connects a Tone.Follower to a bandpass filter (Tone.Filter).
 *          The frequency of the filter is adjusted proportionally to the
 *          incoming signal's amplitude. Inspiration from [Tuna.js](https://github.com/Dinahmoe/tuna).
 *
 *  @constructor
 *  @extends {Tone.Effect}
 *  @param {Frequency|Object} [baseFrequency] The frequency the filter is set
 *                                            to at the low point of the wah
 *  @param {Positive} [octaves] The number of octaves above the baseFrequency
 *                                the filter will sweep to when fully open
 *  @param {Decibels} [sensitivity] The decibel threshold sensitivity for
 *                                   the incoming signal. Normal range of -40 to 0.
 *  @example
 * var autoWah = new Tone.AutoWah(50, 6, -30).toMaster();
 * //initialize the synth and connect to autowah
 * var synth = new Synth.connect(autoWah);
 * //Q value influences the effect of the wah - default is 2
 * autoWah.Q.value = 6;
 * //more audible on higher notes
 * synth.triggerAttackRelease("C4", "8n")
 */
Tone.AutoWah = function(){

	var options = Tone.defaults(arguments, ["baseFrequency", "octaves", "sensitivity"], Tone.AutoWah);
	Tone.Effect.call(this, options);

	/**
	 *  The envelope follower. Set the attack/release
	 *  timing to adjust how the envelope is followed.
	 *  @type {Tone.Follower}
	 *  @private
	 */
	this.follower = new Tone.Follower(options.follower);

	/**
	 *  scales the follower value to the frequency domain
	 *  @type {Tone}
	 *  @private
	 */
	this._sweepRange = new Tone.ScaleExp(0, 1, 0.5);

	/**
	 *  @type {number}
	 *  @private
	 */
	this._baseFrequency = options.baseFrequency;

	/**
	 *  @type {number}
	 *  @private
	 */
	this._octaves = options.octaves;

	/**
	 *  the input gain to adjust the sensitivity
	 *  @type {Tone.Gain}
	 *  @private
	 */
	this._inputBoost = new Tone.Gain();

	/**
	 *  @type {BiquadFilterNode}
	 *  @private
	 */
	this._bandpass = new Tone.Filter({
		"rolloff" : -48,
		"frequency" : 0,
		"Q" : options.Q,
	});

	/**
	 *  @type {Tone.Filter}
	 *  @private
	 */
	this._peaking = new Tone.Filter(0, "peaking");
	this._peaking.gain.value = options.gain;

	/**
	 * The gain of the filter.
	 * @type {Number}
	 * @signal
	 */
	this.gain = this._peaking.gain;

	/**
	 * The quality of the filter.
	 * @type {Positive}
	 * @signal
	 */
	this.Q = this._bandpass.Q;

	//the control signal path
	this.effectSend.chain(this._inputBoost, this.follower, this._sweepRange);
	this._sweepRange.connect(this._bandpass.frequency);
	this._sweepRange.connect(this._peaking.frequency);
	//the filtered path
	this.effectSend.chain(this._bandpass, this._peaking, this.effectReturn);
	//set the initial value
	this._setSweepRange();
	this.sensitivity = options.sensitivity;

	this._readOnly(["gain", "Q"]);
};

Tone.extend(Tone.AutoWah, Tone.Effect);

/**
 *  @static
 *  @type {Object}
 */
Tone.AutoWah.defaults = {
	"baseFrequency" : 100,
	"octaves" : 6,
	"sensitivity" : 0,
	"Q" : 2,
	"gain" : 2,
	"follower" : {
		"attack" : 0.3,
		"release" : 0.5
	}
};

/**
 * The number of octaves that the filter will sweep above the
 * baseFrequency.
 * @memberOf Tone.AutoWah#
 * @type {Number}
 * @name octaves
 */
Object.defineProperty(Tone.AutoWah.prototype, "octaves", {
	get : function(){
		return this._octaves;
	},
	set : function(octaves){
		this._octaves = octaves;
		this._setSweepRange();
	}
});

/**
 * The base frequency from which the sweep will start from.
 * @memberOf Tone.AutoWah#
 * @type {Frequency}
 * @name baseFrequency
 */
Object.defineProperty(Tone.AutoWah.prototype, "baseFrequency", {
	get : function(){
		return this._baseFrequency;
	},
	set : function(baseFreq){
		this._baseFrequency = baseFreq;
		this._setSweepRange();
	}
});

/**
 * The sensitivity to control how responsive to the input signal the filter is.
 * @memberOf Tone.AutoWah#
 * @type {Decibels}
 * @name sensitivity
 */
Object.defineProperty(Tone.AutoWah.prototype, "sensitivity", {
	get : function(){
		return Tone.gainToDb(1 / this._inputBoost.gain.value);
	},
	set : function(sensitivy){
		this._inputBoost.gain.value = 1 / Tone.dbToGain(sensitivy);
	}
});

/**
 *  sets the sweep range of the scaler
 *  @private
 */
Tone.AutoWah.prototype._setSweepRange = function(){
	this._sweepRange.min = this._baseFrequency;
	this._sweepRange.max = Math.min(this._baseFrequency * Math.pow(2, this._octaves), this.context.sampleRate / 2);
};

/**
 *  Clean up.
 *  @returns {Tone.AutoWah} this
 */
Tone.AutoWah.prototype.dispose = function(){
	Tone.Effect.prototype.dispose.call(this);
	this.follower.dispose();
	this.follower = null;
	this._sweepRange.dispose();
	this._sweepRange = null;
	this._bandpass.dispose();
	this._bandpass = null;
	this._peaking.dispose();
	this._peaking = null;
	this._inputBoost.dispose();
	this._inputBoost = null;
	this._writable(["gain", "Q"]);
	this.gain = null;
	this.Q = null;
	return this;
};

export default Tone.AutoWah;

import Tone from "../core/Tone";
