	 * The gain in decibels of the high part
	 * @type {Tone.Gain}
	 * @private
	 */
	this._highGain = new Tone.Gain(options.high, Tone.Type.Decibels);

	/**
	 * The gain in decibels of the low part
	 * @type {Decibels}
	 * @signal
	 */
	this.low = this._lowGain.gain;

	/**
	 * The gain in decibels of the mid part
	 * @type {Decibels}
	 * @signal
	 */
	this.mid = this._midGain.gain;

	/**
	 * The gain in decibels of the high part
	 * @type {Decibels}
	 * @signal
	 */
	this.high = this._highGain.gain;

	/**
	 *  The Q value for all of the filters.
	 *  @type {Positive}
	 *  @signal
	 */
	this.Q = this._multibandSplit.Q;

	/**
	 *  The low/mid crossover frequency.
	 *  @type {Frequency}
	 *  @signal
	 */
	this.lowFrequency = this._multibandSplit.lowFrequency;

	/**
	 *  The mid/high crossover frequency.
	 *  @type {Frequency}
	 *  @signal
	 */
	this.highFrequency = this._multibandSplit.highFrequency;

	//the frequency bands
	this._multibandSplit.low.chain(this._lowGain, this.output);
	this._multibandSplit.mid.chain(this._midGain, this.output);
	this._multibandSplit.high.chain(this._highGain, this.output);
	this._readOnly(["low", "mid", "high", "lowFrequency", "highFrequency"]);
};

Tone.extend(Tone.EQ3, Tone.AudioNode);

/**
 *  the default values
 */
Tone.EQ3.defaults = {
	"low" : 0,
	"mid" : 0,
	"high" : 0,
	"lowFrequency" : 400,
	"highFrequency" : 2500
};

/**
 *  clean up
 *  @returns {Tone.EQ3} this
 */
Tone.EQ3.prototype.dispose = function(){
	Tone.AudioNode.prototype.dispose.call(this);
	this._writable(["low", "mid", "high", "lowFrequency", "highFrequency"]);
	this._multibandSplit.dispose();
	this._multibandSplit = null;
	this.lowFrequency = null;
	this.highFrequency = null;
	this._lowGain.dispose();
	this._lowGain = null;
	this._midGain.dispose();
	this._midGain = null;
	this._highGain.dispose();
	this._highGain = null;
	this.low = null;
	this.mid = null;
	this.high = null;
	this.Q = null;
	return this;
};

export default Tone.EQ3;

import Tone from "../core/Tone";
import "../component/PanVol";
import "../component/Solo";
import "../core/AudioNode";

/**
 *  @class Tone.Channel provides a channel strip interface with 
 *  volume, pan, solo and mute controls. 
 *
 *  @extends {Tone.AudioNode}
 *  @constructor
 *  @param {Decibels} volume The output volume.
 *  @param {AudioRange} pan the initial pan
 *  @example
 * //pan the incoming signal left and drop the volume
 * var channel = new Tone.Channel(-0.25, -12);
 */
Tone.Channel = function(){

	var options = Tone.defaults(arguments, ["volume", "pan"], Tone.PanVol);
	Tone.AudioNode.call(this, options);

	/**
	 *  The soloing interface
	 *  @type {Tone.Solo}
	 *  @private
	 */
	this._solo = this.input = new Tone.Solo(options.solo);

	/**
	 *  The panning and volume node
	 *  @type {Tone.PanVol}
	 *  @private
	 */
	this._panVol = this.output = new Tone.PanVol({
		"pan" : options.pan, 
		"volume" : options.volume,
		"mute" : options.mute
	});

	/**
	 *  The L/R panning control.
	 *  @type {AudioRange}
	 *  @signal
	 */
	this.pan = this._panVol.pan;

	/**
	 *  The volume control in decibels.
	 *  @type {Decibels}
	 *  @signal
	 */
	this.volume = this._panVol.volume;

	this._solo.connect(this._panVol);
	this._readOnly(["pan", "volume"]);
};

Tone.extend(Tone.Channel, Tone.AudioNode);

/**
 *  The defaults
 *  @type  {Object}
 *  @const
 *  @static
 */
Tone.Channel.defaults = {
	"pan" : 0,
	"volume" : 0,
	"mute" : false,
	"solo" : false
};

/**
 * Solo/unsolo the channel. Soloing is only relative to other
 * Tone.Channels and Tone.Solos. 
 * @memberOf Tone.Channel#
 * @name solo
 * @type {Boolean}
 */
Object.defineProperty(Tone.Channel.prototype, "solo", {
	get : function(){
		return this._solo.solo;
	},
	set : function(solo){
		this._solo.solo = solo;
	}
});

/**
 *  If the current instance is muted, i.e. another instance is soloed,
 *  or the channel is muted
 *  @memberOf Tone.Channel#
 *  @type {Boolean}
 *  @name muted
 *  @readOnly
 */
Object.defineProperty(Tone.Channel.prototype, "muted", {
	get : function(){
		return this._solo.muted || this.mute;
	}
});

/**
 * Mute/unmute the volume
 * @memberOf Tone.Channel#
 * @name mute
 * @type {Boolean}
 */
Object.defineProperty(Tone.Channel.prototype, "mute", {
	get : function(){
		return this._panVol.mute;
	},
	set : function(mute){
		this._panVol.mute = mute;
	}
});

/**
 *  clean up
 *  @returns {Tone.Channel} this
 */
Tone.Channel.prototype.dispose = function(){
	Tone.AudioNode.prototype.dispose.call(this);
	this._writable(["pan", "volume"]);
	this._panVol.dispose();
	this._panVol = null;
	this.pan = null;
	this.volume = null;
	this._solo.dispose();
	this._solo = null;
	return this;
};

export default Tone.Channel;

